#go-button
 /*====>Hide the dropmarker<=====margin-right: -32px !important;=====margin-left: 18px !important;=====
 ====>Visible the dropmarker<===== margin-right: -48px !important;=====margin-left: 32px !important;=====*/
 {
  visibility: visible !important;

  margin-left: 32px !important;
  margin-right: -48px !important;
padding: 0px !important;

  list-style-image: url("data:image/png;base64,")!important;
  -moz-image-region: rect(0px 15px 15px 0px)!important;
}
#go-button:hover {
  -moz-image-region: rect(0px 30px 15px 15px)!important;

}

#go-button[disabled="true"] {
  -moz-image-region: rect(0px 45px 15px 30px)!important;
}

#go-button:hover:active {
  -moz-image-region: rect(0px 60px 15px 45px)!important;
}

#urlbar {
  margin-right: 0px !important;
}
