css:
@-moz-document domain("retrotool.io")
{

@font-face {
  font-family: 'Material Icons';
  font-style: normal;
  font-weight: 400;
  src: url(https://fonts.gstatic.com/s/materialicons/v92/flUhRq6tzZclQEJ-Vdg-IuiaDsNcIhQ8tQ.woff2) format('woff2');
}

* {
    font-family: Helvetica, Arial, sans-serif !important;
}

.navbar-menu button {
    text-decoration: none !important;
}

.navbar-menu button:hover {
    color: #00aad6 !important;
}

.navbar-menu button .image {
    background: none !important;
}

.navbar-menu button .image::before {
    font-family: 'Material Icons' !important;
    font-size: 1.8rem;
}

.image.participants::before {
    content: '\e853';
}

.image.actionPoints::before {
    content: '\f23a';
}

.image.export::before {
    content: '\e255';
}

.image.share::before {
    content: '\e80d';
}

.image.options::before {
    content: '\e8b8';
}

.dropdown-menu {
    background: #fff;
    border-radius: 6px;
    box-shadow: rgba(149, 157, 165, 0.4) 0px 8px 24px;
}

.dropdown-menu .dropdown-item:hover, .dropdown-menu .dropdown-item:hover:focus {
    background: #f9f9f9;
    color: #000;
}

.dropdown-menu,
.modal,
.right-panel {
    background: #fff;
    border-radius: 6px;
    box-shadow: rgba(149, 157, 165, 0.4) 0px 8px 24px;
}

.right-panel {
    border-radius: 6px 0 0 6px;
}

.room .boards-container .board .board-cards .public .react-responsive-modal-root .react-responsive-modal-overlay {
    position: fixed;
}

.button.icon-inverted.menu-item .image-label .badge {
    bottom: 0.375em;
    background: #00aad6;
}

@media screen and (max-width: 1279px) {
    .navbar-menu button .image::before {
        font-size: 1.1rem;
    }
}

}
