css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dreamwidth.org")

{
  #page a:visited
   {color: #f4717a !important;}

  #menu a:visited
   {color: #bbbbbb !important;}

  #content
   {font-size: 14px !important;}

  #profile_top
    {padding-top: 10px !important;}

  .userpicdiv
    {float: right !important;
    padding-top: 5px !important;}

  .user_pic_caption
    {display: none !important;}

  .username
    {border: 0 !important;
    background-color: #ffffff !important;}

  .actions ul
    {padding-top: 5px !important;}

  .actions li
    {padding: 0 !important;
    list-style: none;
    border: 0 !important;
    background-color: #ffffff !important;}

  .actions a
    {font-size: 0 !important;}

  .actions img
    {padding-top: 5px !important;}

  .actions span
    {display: inline !important;}

  .profile_join_disabled, .profile_postentry_disabled, .profile_sendmessage_disabled, .profile_addtrust_disabled, .profile_addsub_person_disabled, .profile_addfeed_disabled, .profile_trackuser_disabled
    {font-size: 0 !important;}

  .user_details
    {width: 90% !important;}

  .user_details_inner
    {margin: 0 !important;}

  .details_journal .journal_title
    {font-size: 1.3em !important;
    padding-bottom: 4px !important;}

  .details_journal .journal_subtitle
    {font-size: 1.1em !important;
    padding-bottom: 4px !important;}

  .details_stats p, .journal_adult_warning
    {font-size: 0.8em !important;
    font-weight: normal !important;
    line-height: 135% !important;
    color: #000000 !important;}

  .section
    {border: 0 !important;
    background-color: #ffffff !important;}

  .section_body
    {padding: 0 70px 15px 70px !important;}

}
