css:
@-moz-document domain(erogamescape.dyndns.org), domain(erogamescape.org) {
	
/*======================================

	1-4.Text Module

=======================================*/

pre {
	border-color: #585552;
	color: #c7bbae;
	background-color: #251f1a;
}

/*======================================

	1-5.Hypertext Module

=======================================*/

a:link {
	color: #ade2ff;
}

a:visited {
	color: #d1a9f1;
}


/*======================================

	1-6.List Module

=======================================*/

dd {
	color: #a5a3a1;
}


/*======================================

	1-9.Tables Module

=======================================*/

table {
	background-color: #40392e;
}

th, td {
	border-color: #585552;
}

th {
	color: #f9eada;
	background-color: #eff0f1;
}

td {
	background-color: #40392e;
}

tr.odd td {
	background-color: #1f1914;
}


/*======================================

	1-12.My CSS

=======================================*/

body {
	background-color: #1f1914;
	color: #f9eada;
}

h2 {
	background-color: #384348;
	border-color: #83c2e4;
	color: #e8f7ff;
}

h3 {
	border-color: #556d66;
}

div.odd {
	background-color: #555040;
}

div.even {
	background-color: #433e2c;
}

.red {
	color: #f75e61;
}

table {
	border-color: #585552;
}

th {
	background-color: #796851;
}

a.toContents {
	color: #0f0c0a;
}

.favorite_user h3.summary {
	background-color: #63583c;
}

.mottle_for_comments:nth-child(odd) {
	background-color: #555040;
}

.mottle_for_comments:nth-child(even) {
	background-color: #433e2c;
}

/*======================================

POVのコメント用

=======================================*/

.pov_c_comment .pov_c_title {
	background-color: #40392e;
}

.pov_c_netabare_message {
	background-color: #384348;
}

#touhyou_comments td {
	background-color: #40392e;
}

#touhyou_comments_table td.odd {
	background-color: #555040;
}

#touhyou_comments_table td.even {
	background-color: #40392e;
}

#povlist_contents dl {
	border-color: #c75c81;
}

#povlist_contents dt {
	background-color: #482948;
	color: #ffecff;
	border-color: #c75c81;
}

/*======================================

長文コメントレス用

=======================================*/

.comment_respons td {
	background-color: #433e2c;
}

.comment_respons div .even , .comment_respons div .odd {
	background-color: #433e2c;
}

/*======================================

***_touhyo.php用

=======================================*/

.frame_001 {
	background-color: #503923;
}

/*======================================

user_***.php用

=======================================*/

.dt_table dt {
	background-color: #796851;
}

#hitokoto p.comment{
	background-color: #40392e;
}

dl.dt_table {
	border-left-color: #585552;
	border-bottom-color: #585552;
	border-right-color: #585552;
}

.dt_table dt {
	border-top-color: #585552;
	border-right-color: #585552;
	background-color: #555040;
}

.dt_table dd {
	border-top-color: #585552;
	background-color: #433e2c;
	color: #f9eada;
}


/*======================================

お勧めゲームリスト

=======================================*/

tr.over_75 td, td.over_75 , .over_75 {
	background-color: #355050;
}


/*======================================

その他

=======================================*/

tr.madara td {
	background-color: #433e2c;
}

#pov-area .pov-header {
	background-color: #433e2c !important;
}

/* フォーム系 */

input[type='text'], input[tylpe='text'] /* ← seems typo */, input[type='password'], input[type='submit'], select, textarea {
	background-color: #0f0c0a;
	border-color: #b09f90;
	color: white;
}

/* Twitter Embededへの適用解除 */

html.SandboxRoot body {
	background-color: white;
	color: #0f0c0a;
}

html.SandboxRoot body a {
	color: #2b7bb9;
}

html.SandboxRoot body h2 {
	background: inherit;
	color: inherit;
}

/* TOPページ用 */

#campaignlist td {
	color: #f9eada;
}

div#comment_res table tr:nth-child(even) td {
	background-color: #433e2c;
}

div#comment_res table tr:nth-child(odd) td {
	background-color: #555040;
}

div#comment_touhyou table tr:nth-child(even) td {
	background-color: #433e2c;
}

div#comment_touhyou table tr:nth-child(odd) td {
	background-color: #555040;
}

div.recent_recommendations_game {
	border-bottom-color: #6c6a67;
}

/* attlist用 */

h4.gray {
	background-color: #2f2925;
}

/* game用 */

#soft-title {
	border-top-color: #585552;
	border-bottom-color: #585552;
}

.expectation_game {
	border-color: #585552;
}

#att_pov_table .netabare, #att_pov_table .netabare a:link,
#att_pov_table .netabare a:visited {
	color: #40392e;
	opacity: 0;
}

/* memoページ用 */

#main_contents {
	color: #f9eada;
}

#memo span[style='color: #F8F8DF;'] {
	/* ネタバレ防止で背景と同化、念入りに不透明度0% */
	background-color: #1f1914;
	opacity: 0;
}

/* mod_gameページ用 */

#att li {
	background-color: #40392e;
	border-color: #585552;
}

#att li.att_checked {
	background-color: #684035;
	color: white;
}

/* musicページ用 */

#fixed_comment_data {
	background-color: #433e2c;
	color: #f9eada;
}

#fixed_tokuten_data {
	color: #f75e61;
}

/* povページ用 */

div.pov:nth-child(odd){
	background-color: #555040;
}

div.pov:nth-child(even){
	background-color: #433e2c;
}

/* user_informationページ用 */

#tab li {
	color: #1f1914;
}

/* 以下は半ば強引な変え方 */

[style='color:black;'], [style^='color: black;'] {
	color: white !important;
}

[style^='color: blue;'] {
	color: #83c2e4 !important;
}

div[style*='background-color: white;'] {
	background-color: #0f0c0a !important;
}

a[href] + span[style='color:gray;'] {
	color: #fcf5ed !important;
}

p[style='background-color:#FFE899;'] {
	background-color: #63583c !important;
}

div[style^='background-color: #fff0c0;'] {
	background-color: #555040 !important;
}

[style*='background-color:#FFE899;'] {
	background-color: #534933 !important;
}
	
}

@-moz-document regexp("https?\:\/\/erogamescape\.(dyndns\.)?org\/~ap2\/ero\/toukei_kaiseki\/contents_game\.php.*") {
/* contents_game用 */

input[type='text']:focus, input[type='number']:focus {
	background-color: #01264f !important;
}
}
