css:
@-moz-document domain("nakama.social") {
:root {
    --fondo: url("/*[[wall]]*/");
    --ancho-pagina: /*[[fw]]*/;
    --transparencia: calc((100 - /*[[trns]]*/)/100);
    --desenfoque: /*[[blur]]*/;
}

body {
  	background-image: var(--fondo) !important;
	background-attachment: fixed !important;
	background-position:50% 0px !important;
	background-size: auto auto !important;
  	background-repeat: no-repeat !important;
}

.container {
    max-width: var(--ancho-pagina) !important;
}

.container h1, .container > h2 {
    text-shadow: 0px 0px 5px rgba(0,0,0,var(--transparencia));
}

header, .post, input[type=email], input[type=search], input[type=text], .notifications, .notifications-controls, .post-form-controls, .post-form > textarea {
    background-color: rgba(0,0,0,var(--transparencia));
    color: #fff;
    backdrop-filter: blur(var(--desenfoque));
    box-shadow:0 0 3px rgba(0,0,0,calc(var(--transparencia)*0.6));
}

.notifications, .notifications-controls, .post-form-controls {
    padding: 10px 10px;
}

/*[[brd]]*/
}

style_setting_options.value:
.post, .btn, button, input, textarea, .post-warning, .media-scroller audio, .media-scroller iframe, .media-scroller img, .media-scroller video, .avatar {
    border-radius: 0;
}
