css:
@-moz-document domain("postimages.org"), domain("postimg.cc") {
:root {
    --font-family: 'Sitka Small';
    --blue: #8eb5f2;  
    --black: #000;
    --white: #fff;
    --text: #f1f1f1;
    --accent: #999;
    --headers: #9a9a9a;
    --border: #222;
    --content-bg: #191919;
    --header-bg: #111;
    --link: #8a8aff;
    --link-hover: #ffed85;
}
body { background-color: var(--black) !important; color: var(--text); font-family: var(--font-family) }
#sb-main, .gallery-list {
    background: none;
}
.uploaddrop {
    background-color: var(--header-bg);
}
.queue-item {
    background: var(--content-bg);
}
h1, h2, h3, h4, h5 {
    color: var(--headers)
}
header, .gallery, .thumb, .plans > div:nth-child(1) > .pricing-plan {
    background-color: var(--header-bg);
    border-color: var(--border)
}
.frontpage #content, .modal-content, #content, .panel { background-color: var(--content-bg); }
a {
    color: var(--link);
}
a:hover, a:focus {
    color: var(--link-hover);
}
.bottom-menu a.active, .logins a.active, .logins span.active, .mainmenu a.active, .mainmenu span.active, .thumb .imagetitle { color: var(--white) }

select, select:focus {
    background: var(--black);
    border-color: var(--border);
}

.logo img {
    filter: invert(100%) hue-rotate(180deg)
}

.btn {
    border-color: var(--blue);
    color: var(--blue);
    background: var(--black);
}
.btn-group .btn:not(.active):hover {
    background: var(--content-bg);
    color: var(--text);
    border-color: var(--text)
}
.btn:hover, .btn:focus, .btn-group .btn.active, .panel input:not([type=submit]):not([type=reset]):not([type=checkbox]):not([type=radio]):not([type=file]):not([type=image]):focus,
.panel textarea:focus {
    background-color: var(--blue);
    color: var(--black);
}

a.cover[style="background-image:url('//postimgs.org/img/empty.png')"] {
    filter: invert(100%)
}

input:not([type=submit]):not([type=reset]):not([type=checkbox]):not([type=radio]):not([type=file]):not([type=image]),
textarea, .panel input:not([type="submit"]):not([type="reset"]):not([type="checkbox"]):not([type="radio"]):not([type="file"]):not([type="image"]), .panel textarea {
    background: var(--header-bg);
    border-color: var(--border);
    color: var(--text);
}
input:not([type=submit]):not([type=reset]):not([type=checkbox]):not([type=radio]):not([type=file]):not([type=image]):focus,
textarea:focus, .gallery h3:focus,
.gallery h3[contenteditable=true]:hover {
    background: var(--content-bg);
    border-color: var(--blue);
    color: var(--blue);
}

.modal-header, .logins a:focus,
.logins a:hover,
.mainmenu a:focus,
.mainmenu a:hover  {
    background-color: var(--blue);
    color: var(--black);
    border-color: var(--border);
}
.modal-body div[style*="border-top: 1px solid #e8e8e8"], hr, .input-group .btn {
    border-color: var(--border) !important
}
textarea {
    transition: none !important;
}
.gallery:hover, .thumb:hover  {
    -moz-box-shadow:0 0 5px var(--blue);
    -webkit-box-shadow:0 0 5px var(--blue);
    box-shadow:0 0 5px var(--blue)
}
.panel .control-label {
    color: var(--blue);
}
.gallery .count, .copyright, .bottom-menu li:not(:first-child)::before {
    color: var(--accent)
}
*[style*="color: #808080"] {
    color: var(--accent) !important
}
a.btn[href*="https://postimages.org/plans"] {
    display: none !important;
}
.image-controls {
    background: var(--header-bg)
}
#uploadqueue {
    background: var(--content-bg) !important;
}
}
