/* ==UserStyle==
@name Minecraft Fandom Wiki Fixes
@version 1.0.0
@description They screwed up everything, this fixes it.
@namespace lordpipe
@author lordpipe
@homepageURL https://github.com/lordofpipes/minecraft-wiki-fixes
@supportURL https://github.com/lordofpipes/minecraft-wiki-fixes/issues
@updateURL https://raw.githubusercontent.com/lordofpipes/minecraft-wiki-fixes/master/minecraft-wiki-fixes.user.css
@license CC-BY-4.0
@var text font "Choose a font" Noto Sans
==/UserStyle== */

@-moz-document domain("minecraft.fandom.com") {
  body {
    font-family: "/*[[font]]*/", "Rubik", "Liberation Sans", sans-serif !important;
  }

  .resizable-container {
    max-width: 999999px;
  }

  .main-container {
    width: 100%;
    margin-left: 0px;
  }

  .wds-global-footer,
  #mixed-content-footer,
  #ds_cpp_inner,
  #WikiaBar,
  .global-navigation,
  .fandom-sticky-header,
  .page__right-rail,
  .wds-global-footer,
  .gpt-ad,
  #ds_cpp_inner,
  .ds_im,
  .ds_cpp
  {
    display: none;
  }
}
