font-family: /*[[FontMain]]*/ !important;
color: #014A8F !important;
text-decoration: /*[[Underline]]*/ !important;
font-weight: bold !important;
}
/*bluelink-划过*/
.bluelink:hover {
color: #0000FF !important;
text-decoration: /*[[Underline]]*/ !important;
}
/*bluelink-已访问*/
.bluelink:visited {
color: #4B0F68 !important;
}

/*搜索的文字*/
em {
font-weight: bold !important;
background: yellow !important;
color: #4B0F68 !important;
padding: 1px 1px 1px 1px !important;
border: 1px solid gray !important;
border-radius: 3px;
}
/*内容*/
div.p_content {
font-family: /*[[FontMain]]*/ !important;
}
/*MediaContent*/
div.p_mediaCont {
border: 1px solid lightgray !important;
box-shadow: 0px 0px 2px 0px Gray !important;
}
div.p_mediaCont:hover {
border: 1px solid gray !important;
box-shadow: 0px 0px 5px 0px Gray !important;
}

/*搜索的文字 旧*/
font[style="color:#e10900"] {
font-weight: bold !important;
background: yellow !important;
color: #4B0F68 !important;
padding: 1px 1px 1px 1px !important;
border: 1px solid gray !important;
border-radius: 3px;
}
}

/*[Level1]贴吧菜单iFrame*/
@-moz-document url-prefix("http://tieba.baidu.com/tb/coloregg/ceIndex.html"),
url-prefix("http://post.baidu.com/tb/coloregg/ceIndex.html") {
#frameContainer {
box-shadow: 0px 0px 5px 0px Gray !important;
}
}
