css:
@-moz-document domain("problemkaputt.de") {
/*
* Berny23's Modern Design for problemkaputt.de v1.0
*/
font[size="-1"] {
    font-size: 1em;
}

p {
    margin-bottom: 2em;
}

table {
    border-collapse: collapse;
}

td {
    padding: 0.3em;
}

img {
    width: auto;
}

font[size="+1"] {
    font-weight: bold;
}

center > table + table tr :first-child img {
    display: none;
}

center > table table table table tr {
    background: /*[[color-secondary]]*/;
    border-left: 0.5em solid /*[[color-primary]]*/;
}

center > table table table table tr:hover {
    background: /*[[color-primary]]*/;
}
}
