css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
ASCII.jp
http://ascii.jp/
*/

@-moz-document url-prefix("http://ascii.jp/elem/")
, url-prefix("http://www.ascii.jp/elem/") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #dcdcd4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #dcdcd4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#jpWrap
, #jpWrap div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#jpWrap * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#jpWrap a:link { color : #0000ee !important; }
#jpWrap a:visited { color : #551a8b !important; }
#jpWrap a:hover { color : #ee0000 !important; }
#jpWrap a:hover { background-color : transparent !important; }
/* /destroy div layout */

#toolMenu
, #gMenu
, #subMenu
, .pickwrap
, .sbm
, #sideRank
, #sideL
, .artsCont .fr
, .rellst .fr
{ display : none !important; }

h1
{ margin : 0 !important; }

h1 a
{ padding : 0 !important; }

}
