 }

/*Profile Background*/

.dark-theme .section-content {
    background-color: #0C0E10;
    color: #95BA74;
}.dark-theme .rbx-tabs-horizontal .nav-tabs {
    background-color: #95BA74;
    color: #000;
}.dark-theme .profile-avatar-right .profile-avatar-mask {
    background-color: #9EA894;
    color: #95BA74;
}.profile-avatar-left .enable-three-dee {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    border: 1px solid transparent;
    background-color: #95BA74;
    border-color: #393b3d;
    color: #000000;
    cursor: pointer;
    display: inline-block;
    font-weight: 500;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 9px 9px;
    font-size: 16px;
    line-height: 100%;
    border-radius: 3px;
    border: 1px solid #393b3d;
    position: absolute;
    top: 12px;
    right: 12px;
    min-height: 32px;
}.dark-theme .container-footer {
    background-color: #000; 
   }

/* Play button colour*/

@keyframes rainbow {
    0% {background-color: #95BA74;border-color: #95BA74;}
    14% {background-color: #5A7145;border-color: #5A7145;}
    28% {background-color: #95BA74;border-color: #95BA74;}
    42% {background-color: #5A7145;border-color: #5A7145;}
    56% {background-color: #95BA74;border-color: #95BA74;}
    80% {background-color: #5A7145;border-color: #5A7145;}
    94% {background-color: #95BA74;border-color: #95BA74;}
    100% {background-color: #95BA74;border-color: #95BA74;}
}

.btn-common-play-game-lg {
    animation: rainbow 10s infinite;
}
