css:
@-moz-document url-prefix("https://feedless.social/") {
body {
  background: #3e4349;  
}

.home-page {
    background: #181b27;
    color: white;
}
.screen-center {
    background:#181b27;
    color:white;
    max-width: 95%;
}

.link-block {
    color: white;
}

.logo {
filter: invert(100%);
}


.screen-center {
}

.columns {
    max-width: 100%;
}

h2 {
  color: gray;  
}

h1 {
    font-size: 30px;
}
.column-side-padding {
    color: gray;
}

.community-topic-name {
    color: white;
}
.secret-chat {
    background: #30374f;
}
.wall {
     background: #30374f;
    max-width: 70%;
}
.friends-communities {
     background: #30374f;
    max-width: 30%;
}
.secret-button {
    background: white!important;
    color: red;
    height: auto;
    border: 1px white solid;
    width: 70%;
    margin: auto;
}

}

@-moz-document url("https://feedless.social/communities/programming"), url("https://feedless.social/communities"), url("https://feedless.social/about") {
.screen-center {
    width: 70%!important;
}

input[type="text"] {
    width: 500px;
}
}

@-moz-document url("https://feedless.social/") {
.index-welcome {
    background: #181B27;  
}

.index-explanation {
    background: #181B27;  
}

a:link {
  color: orange!important;
}

a:visited {
  color: orange!important;
}


a:hover {
  color: hotpink!important;
}


a:active {
  color: orange!important;
}

.button-blue {
    background: #505d84!important;  
    font-weight: bold;
}
}
