css:
@-moz-document domain("lkong.com") {
/** 全局 */

/* 定义变量 */
body {
	/* 计算边距，以适应大屏，使得主区域宽度至多1500px。*/
	--margin: max(80px, calc(50% - 750px));
}

/* 广告 */
.lkong-ads {
  display: none;
}

/* 缩减页眉尺寸 */
.css-hls02m,
.css-7o8ir {
	height: 40px;
}

.css-i97t1s,
.badge-wrap{
	line-height: unset;
}


/* 主区域边距精确化 */
.css-7o8ir,
.css-83qgcm{
	width: unset;
	padding: 0 var(--margin);
	margin: unset;
}

/* 优化列表项 */
h4~div ul {
	margin-left: 1.5em;
}
h4~div  li::before {
	margin: 0 0em 0 -1em;
}

h4~div  li {
	text-indent: -0.5em;
	overflow-wrap: break-word;
	text-align: justify;
}

/* 页码去掉圆圈 */
.ant-pagination-item {
	border: none;
}
}

@-moz-document url("https://www.lkong.com/"), url("https://www.lkong.com/feeds") {
/** 首页 */

/* 论坛名多列显示 */
.css-1cw8ku2~div {
	width: 100%;
	display: flex;
	flex-flow: wrap;
	justify-content: space-between;
}

.css-1fii08f {
	width: 320px;
}

/* 较高屏蔽右栏固定位置 */
@media (min-height:750px){
	.css-x4oy6m{
		max-height: 750px;
		position: sticky;
		top: 50px;
	}
}

/* 热门置于ID前 */
.css-x4oy6m{
	display: flex;
	flex-direction: column;
}

.css-x4oy6m>div:nth-child(3) {
	order: -1;
}
}

@-moz-document url-prefix("https://www.lkong.com/forum/") {
/** 子论坛页 */

/* 论坛名放到页尾 */

.css-1xljy9y {
  display: flex;
	flex-direction: column-reverse;
}

.forum-top {
	width: unset !important;
	margin: 0 var(--margin) !important;
}

/* 发表帖子按钮置于页面顶端 */
@media (min-width:992px){
	.right .ant-btn-primary {
		position: fixed;
		top: 5px;
		left: calc(270px + var(--margin));
		z-index: 10;
		width: 120px;
		height: 30px !important;
		padding: 0;
	}
}

@media (max-width:991px){
	.right .ant-btn-primary span {
		text-indent: 5px;
	}
	.right .ant-btn-primary span:before {
		
		content: url('data:image/svg+xml;%20charset=utf8,%3C?xml%20version=%221.0%22%20standalone=%22no%22?%3E%3C!DOCTYPE%20svg%20PUBLIC%20%22-//W3C//DTD%20SVG%201.1//EN%22%20%22http://www.w3.org/Graphics/SVG/1.1/DTD/svg11.dtd%22%3E%0A%3Csvg%20version=%221.1%22%20xmlns=%22http://www.w3.org/2000/svg%22%20viewBox=%2264%2064%20896%20896%22%20focusable=%22false%22%20data-icon=%22plus%22%20width=%221em%22%20height=%221em%22%20fill=%22currentColor%22%20aria-hidden=%22true%22%3E%3Cdefs%3E%3Cstyle%3E%3C/style%3E%3C/defs%3E%3Cpath%20d=%22M482%20152h60q8%200%208%208v704q0%208-8%208h-60q-8%200-8-8V160q0-8%208-8z%22%3E%3C/path%3E%3Cpath%20d=%22M176%20474h672q8%200%208%208v60q0%208-8%208H176q-8%200-8-8v-60q0-8%208-8z%22%3E%3C/path%3E%3C/svg%3E')
	}
	.right .ant-btn-primary {
		position: fixed;
		top: 5px;
		left: 172px;
		z-index: 10;
		height: 30px !important;
		padding: 0;
		background: none;
		border: none;
		box-shadow: none;
		text-shadow: none;
		width: 30px;
		overflow: hidden;
	}
}

.css-1xrb416{
	margin-bottom: unset;
}

.css-b7o9sn {
	padding: 0;
}

/* 有n条新帖子栏 */
.threads>div:nth-child(2) {
	margin: 0;
}


/* 较高屏蔽右栏固定位置 */
@media (min-height:750px){
	.css-1g2kfon{
		max-height: 750px;
		position: sticky;
		top: 50px;
	}
}

/* 热门置于ID前 */
.css-1g2kfon{
	display: flex;
	flex-direction: column-reverse;
	justify-content: flex-end;
}
}

@-moz-document url-prefix("https://www.lkong.com/thread/") {
/** 帖子页 */

/* 标题栏*/

.css-1yfjitf {
	padding: 8px 0;
	width: unset;
	margin: 0 var(--margin);
}
.thread-header-main {
	display: flex;
	flex-flow: row wrap;
	justify-content: flex-end;
	align-items: center;
}
.thread-title {
	margin-bottom: 0;
  width: 100%;
}

.thread-title~div:last-child {
	margin-right: 0;
}

.css-116wftn{
  margin-bottom: 2px;
}

/* 左边头像栏 */
.css-1v7uwc5,
.css-iz0yxa {
	margin-right: 2px;
  min-height: unset;
  padding: 10px;
}

/* 头像 */
.css-s98rak,
.avatar-wrapper {
  height: 162px !important;
  width: 162px !important;
  border: none !important;
  border-bottom: 1px solid rgb(238, 238, 238) !important;
  border-radius: 0 !important;
}
.css-s98rak span,
.avatar-wrapper span {
  width: 100% !important;
  height: 100% !important;
}

/* ID */
.user-wrapper {
  margin: 5px !important
}


/* 跟帖信息栏 */
.css-dz4ra9 {
	padding: 3px 0;
}

/* 发表时间 */
.left-area {
	margin-left: 10px !important;
}

/* 楼号 */
.lou {
  margin-right: 10px !important;
}

/* 帖子内容 */
.main-block {
	padding: 5px 10px !important; 
}
.thread-content p {
	margin: 0;
}
/* 引用 */
.css-h0ave8 {
	padding: 5px;
}

/* 龙晶 */
.css-e7z895{
	margin: 5px 10px 0px;
}

/* 评分 */
.css-1arbysm {
  margin: 40px 10px 10px;
}
.css-1arbysm table {
	margin-top: 5px;
}
.css-1arbysm table td{
	padding: 2px;
}

/* 回复/评分/收藏/举报栏*/
.css-9ph873 {
	padding: 0 10px 5px;
}

/* 回复表单 */
.css-1mtj6bn {
	padding: 10px 10px 0;
}

.editor-bottom{
	align-items: center;
}
}

@-moz-document url-prefix("https://www.lkong.com/new/"), url-prefix("https://www.lkong.com/edit/"), url-prefix("https://www.lkong.com/reply/") {
/** 发帖回帖页 */

.css-1fcdc3s .ant-affix {
	top: 40px !important;
}
.editor-header,
.editor-main{
	width: unset !important;
	padding: 0 80px !important;
	margin: unset !important;
}
.css-1d0y4b9 {
	padding: unset;
}

.css-1d0y4b9 button:first-child {
	margin: 0 2px 0 0;
	padding: 0 6px 0 0;
}

.editor-main {
	margin-top: 10px !important;
}

.css-9xr9bw {
  padding: 0;
}
.editor-main>div:nth-child(2) {
	padding: 10px !important;
}
}
