css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(google.co.uk), domain(google.com), domain(google.pt), domain(google.de), domain(google.fr)
{
html, body, div, td, br, input, font, form.gs, .gs {font-family:Arial !important; font-size:10pt !important; color:#000 !important; text-align:left !important; padding:0px !important; margin:0px !important;}
span  {color:#000 !important; font-style:italic !important; margin-top:0.3em !important; width:75% !important;}
a:link, a:visited, a:active {text-decoration:none !important; color:#000 !important;}
a:hover {text-decoration:underline !important;}
div,td {color:#000 !important;}
.fl:link,.fl:active,.fl:visited,.fl:hover {color:#000 !important;}
.i,.i:link,.i:active,.i:visited,.i:hover {color:#00C !important; font-weight:bold !important;}
.l,.l:link,.l:active,.l:visited,.l:hover {color:#000 !important; text-decoration:underline !important;}
.z {display:none !important;}
div.n {margin-top:1ex !important;}
.n a {font-size:10pt; color:#000 !important;}
.n a:hover {color:#ff0000 !important; text-decoration:none !important; font-weight:bold !important;}
.q:visited,.q:link,.q:active,.q {color:#000 !important;} 
.b {font-size:10pt !important; font-weight:bold !important; color:#00C !important; margin:0.2em !important;}
.g {margin-top:2em !important; width:90% !important; border:1px #000 inset !important; padding:1em !important;}
td.k {display:none !important;}
td, table, html {background:#fff !important;}
td, table {left:0px !important; padding:1px !important; margin:1px !important;}
p img {float:right !important; margin-bottom:20px !important;}
font.p, center {display:none !important;}
p.g, td.j {background:#fff !important; width:90% !important;border:0px !important;}
}
