css:
@-moz-document domain("twitch.tv") {
.reward-icon--large {
	height: 2.0rem;
	width: 9.3rem;
}

.reward-icon__image--raised {
	position: absolute;
	margin-top: 0.1em !important;
	margin-bottom: 0 !important;
	margin-left: 5em;
    width: 1.2em;
}
.reward-icon__cost {
	margin-right: 1em;
	margin-top: 0 !important;
	margin-bottom: 0 !important;
}

.reward-list-item {
    margin-bottom: 0.3em !important;
}


.reward-list-item > div > div > p {
    margin: 0.1em !important;
    font-size: 75% !important;
}

.reward-list-item > div > div {
    margin: 0 !important;
    padding: 0 !important;
}

}
