css:
@-moz-document url-prefix('https://www.youtube.com/') {
	#secondary, #voice-search-button, #gs_st50, #search-icon-legacy, #top-level-buttons-computed > ytd-button-renderer:nth-child(3),ytd-button-renderer.style-text[is-icon-button] #text.ytd-button-renderer, ytd-button-renderer.style-default[is-icon-button] #text.ytd-button-renderer, ytd-button-renderer.style-opacity[is-icon-button] #text.ytd-button-renderer, #container > ytd-expander > ytd-metadata-row-container-renderer{
		display:none;
	}
	#search {
	    text-align: center;
	}
	#search-form{
	    border-right:1px solid var(--ytd-searchbox-legacy-border-color);
	}
	#buttons.ytd-masthead > ytd-topbar-menu-button-renderer[is-icon-button][has-no-text], #movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-left-controls > a.ytp-next-button.ytp-button, #movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-right-controls > button:nth-child(1),#movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-right-controls > button:nth-child(9),#movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-right-controls > button.ytp-size-button.ytp-button, #movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-right-controls > button.ytp-miniplayer-button.ytp-button, #movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-right-controls > button.ytp-subtitles-button.ytp-button, #movie_player > div.ytp-player-content.ytp-iv-player-content > div.annotation.annotation-type-custom.iv-branding, #movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-left-controls > a.ytp-prev-button.ytp-button, #movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-right-controls > button.ytp-fullerscreen-edu-button.ytp-button.ytp-fullerscreen-edu-button-subtle > div.ytp-fullerscreen-edu-chevron, #footer,#sections > ytd-guide-section-renderer:nth-child(3), #sections > ytd-guide-section-renderer:nth-child(4), #section-items > ytd-guide-entry-renderer:nth-child(2){
		display:none;
	}
	.ytp-exp-bottom-control-flexbox .ytp-left-controls{
		flex:-1;
		-webkit-flex:inherit;
	}
	.ytp-exp-bottom-control-flexbox .ytp-chrome-controls{
		justify-content:center;
	}
	#movie_player > div.ytp-chrome-top > div.ytp-chrome-top-buttons, #youtube-paths > path:nth-child(4),  #youtube-paths > path:nth-child(5), #header-entry, #movie_player > div.ytp-chrome-bottom > div.ytp-chrome-controls > div.ytp-right-controls > button.ytp-fullerscreen-edu-button.ytp-button {
		display:none;
	}
	#movie_player > div.ytp-chrome-top > div.ytp-title{
		text-align:center;
	}
	.ytp-chrome-top{
		top:80%;
	}
	#ytp-id-17{
	    position: fixed;
	    top: 65%;
	    left: 50%;
	    -webkit-transform: translate(-50%, -50%);
	    transform: translate(-50%, -50%);
	}
	#country-code, #metadata-line > span:nth-child(2){
		display:none;
	}
}
