/* ==UserStyle==
@name           Overleaf Dark Theme
@namespace      USO Archive
@author         sophie_illy
@description    `Dark Theme for the UI and optional dark theme for the pdf viewer.Edit the CSS code to uncomment the part in question.`
@version        20210719.16.51
@license        CC-BY-ND-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document url-prefix(https://www.overleaf.com/project/) {
.pdfjs-viewer {
    filter: /*[[change_pdf_viewer]]*/;
}

.CodeMirror-code {
     color: #c4c4c4;
    background-color: #2b2b30;
}
        
    /*mudar o tipo de letra do texto*/
    /*Mudar o tipo de letra dos numeros do gutter*/

.CodeMirror-gutter {
     background-color: #3c3c44;
    font-family: Open Sans, Helvetica, Sans-Serif;
}
    
.CodeMirror-linenumber {
    color: #f0f0f0;
}    
    

    
.rich-text .CodeMirror,
.rich-text .CodeMirror pre {
 font-family: Open Sans, Helvetica, Sans-Serif;
}
    
    
/*gutter*/
.ace-overleaf .ace_gutter {
    background: #3c3c44;
    color: #f0f0f0;
}

.ace-overleaf .ace_gutter-active-line {
    background-color: #2c3645;
}

.main-content {
    background-color: #2b2b30;
}

body {
    background-color: #2b2b30;
}

.ace-overleaf {
    background-color: #2b2b30;
    color: #c4c4c4;
}

.ace-content {
    color: #e8e8f0;
}

.pdf-viewer {
    background-color: #2b2b30;
}

}
