css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://forums.whirlpool.net.au/) {

* {
 background:none !important;
  background-color:white !important;
  color:black !important;
  font-family:Georgia !important;
  border:none !important;
}
body{
  font-size:1em !important;
}
a{
  color:#004276 !important;
}
h3{ 
  width:568px !important;
 text-align:center !important;
/***   display:none !important;
 text-align:center !important;

  position:absolute !important;
  word-wrap:break-word !important;
  overflow:hidden !important;
margin-left:-28px !important;
line-height:15px !important;
height:200px !important;
width:16px !important;
opacity:0.5 !important;
padding:1px !important;
***/
}
#root{
width:100% !important;
}
#logo, #logosub, #left{
  display:none !important;
}

#content{
  margin:0 auto!important;
  margin-left:20px !important;
  border:none !important;
  padding:1.5em !important;
}
#index{
  margin:0 !important;
}
#index>div.column{
 width:50% !important;
}
#index .column colgroup{
  display:none !important;
}

#index .column .inner{
  margin:0 !important;
}
#index .column .inner table {
 max-width:655px !important;
  margin:0 !important;
margin-bottom:12px !important;  overflow:
hidden !important;
}
#index .column .inner table tbody tr{
  float:left !important;
  border-left:1px solid black !important;
  border-top:1px solid black !important;
  border-bottom:1px solid black !important;
  margin:0 !important;
  padding:0 !important;
  width:325px !important;
  height:180px !important;
  overflow:hidden !important;
}

#index>div.column .inner tbody tr:nth-child(even){
  border-right:1px solid black !important;
}
#index>div.column .inner tbody>tr:nth-child(2)~tr{
  border-top:none !important;

}
#index>div.column .inner tbody tr:last-child{
  border-right:1px solid black !important;
}


#index td.threads{
  float:left !important;
  height:50px !important;
  width:95% !important;
  border:none !important;
}
#index td.threads>div{
margin:0.35em 0.5em !important;
}
#index td.threads>div{
  font-size:11px;
}
#index td.threads>div>a{
  font-size:1.6em !important;
}
#index td.title{
  float:left !important;
  width:95% !important;
  margin:0 auto;
  border-bottom:1px solid black !important;
  color:black !important;
  padding:0.5em !important;
}
#index td.title>div.title,#index td.title>div.subtitle{
  width:100%;
  text-align:center;
  font-size:0.8em !important;
}

#index td.title a{
  color: black !important;
}

#content>span.shim1, #content>span.shim2, #footer, #breadcrumb{ 
  display:none !important; 
}



}
