css:
/* move tabs to top */

.tabbrowser-strip {
  top: -3px !important;
  position: fixed !important;
  width: 100% !important;
}

.tabbrowser-strip:-moz-system-metric(windows-compositor) {
  left: 1px !important;
}

.tabbrowser-strip .tabbrowser-tabs {
  width: 100% !important;
  z-index: 0 !important;
}

/* bottom tabstrip */

.tabs-bottom {
  -moz-border-image: url("data:image/png;base64,") 0 23 3 23 / 0 23px 3px 23px !important;
}

/* fix tab height */

.tabbrowser-tab,
.tabs-newtab-button,
.tabbrowser-arrowscrollbox > .scrollbutton-up,
.tabbrowser-arrowscrollbox > .scrollbutton-down {
  margin-top: 3px !important;
}

/* reposition tab drop arrow */

.tab-drop-indicator-bar {
  position: fixed !important;
  top: 0 !important;
  width: 100% !important;
  z-index: 1 !important;
}

/* move window down for tab space */

#main-window {
  margin-top: 26px !important;
}

#main-window #navigator-toolbox {
  -moz-border-image: url("chrome://browser/skin/nav-bar.png") 0 2 3 2 / 0px 2px 3px 2px !important;
  padding: 0 !important;
}

#main-window #nav-bar {
  margin-top: 1px !important;
  margin-bottom: -3px !important;
}

.tabbrowser-tab,
.tabs-newtab-button {
  margin-bottom: 1px !important;
}

#menubar-items {
  padding: 0 !important;
}

.tabbrowser-tab[selected="true"] {
  -moz-border-image: url("data:image/png;base64,") 2 10 2 10 / 2px 10px 2px 10px !important;
}

.tabbrowser-arrowscrollbox > .scrollbutton-up,
.tabbrowser-arrowscrollbox > .scrollbutton-down {
  margin-bottom: 1px !important;
}

#navigator-toolbox > toolbar {
  background: none !important;
  padding-left: 2px !important;
  padding-right: 2px !important;
}

sidebarheader {
  margin-left: 0 !important;
  background: none !important;
  border-right: 1px solid rgba(0,0,0,0.5) !important;
}

/* fix popup window contents */

#main-window[chromehidden*=" "] {
  margin-top: 0px !important;
}

#main-window[chromehidden*=" "] #nav-bar {
  height: 28px !important;
  margin-bottom: 0 !important;
}

#main-window[chromehidden*=" "] #navigator-toolbox {
  -moz-border-image: none !important;
}

#main-window[chromehidden*=" "] .tabbrowser-strip {
  visibility: hidden !important;
}

/* fix StrataHomeTab placement */
.home-tab-button {
  -moz-appearance: none;
  -moz-image-region: auto;
  border: none;
  margin-top: 0px !important;
  margin-right: -9px !important;
}

.home-tab-button:hover:active {
  -moz-appearance: none;
  -moz-image-region: auto;
  border: none;
  margin-top: -2px !important;
  margin-right: -8px !important;
  margin-left: -1px !important;
}
