@-moz-document domain(10fastfingers.com) {

/* GLOBAL VARIALBES */
:root {
  --font: "Poppins", sans-serif;

  /* Preset */
  --bgn: #2b2f3a; /* main bg color */
  --bgd: #363b4b; /* darker highlight color */
  --bgh: #2d323e; /* lighter highlight color */
  --bgt: #ffffff; /* faded text color */
  --bgo: #ffffff; /* text color */
  --bga: #00ff7f; /* accent color */

  /* red/green/blue/yellow colors */
  --bgr: #e74c3c; /* red color */
  --bgg: #26a65b; /* green color */
  --bgb: #3a539b; /* blue color */
  --bgy: #ffd724; /* yellow color */

  /* test colors */
  --bgb: #467bc6; /* blue color */
  /*--bgn: #2c3e50; /* main bg color */
  /*--bgd: #34495e; /* darker highlight color */
  /*--bgd: #994f5c; /* pinkish red color */
  /*--bgd: #4f5c99; /* purpleish blue color */
}

/* Timer Hide */
#speedtest-main #input-row .hide-time {
  color: var(--bgd) !important;
}
/* Scrollbar */

::-webkit-scrollbar-track {
  -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
  border-radius: 10px;
  background-color: #212121;
}

::-webkit-scrollbar {
  width: 12px;
  background-color: #212121;
}

::-webkit-scrollbar-thumb {
  border-radius: 10px;
  background-color: var(--bgd) !important;
}

/* global styles */
*,
*::before,
*::after {
  border: none !important;
  box-shadow: none !important;
  box-sizing: border-box !important;
  text-shadow: none !important;
  text-decoration: none !important;
  font-family: var(--font) !important;
  border-radius: 0 !important;
}

/* close cross button */

.close {
  color: var(--bgt);
}

/* icon font */
.fa,
.fa::before,
.fa::after {
  font-family: "FontAwesome" !important;
}

/* icon font */
.glyphicon,
.glyphicon::before,
.glyphicon::after {
  font-family: "Glyphicons Halflings" !important;
}

/* default text color */
div,
td,
h3 {
  color: var(--bgt) !important;
}

/* text styles */
a {
  color: var(--bgt) !important;
}

/* correct */
.correct,
#correct .value strong,
#wpm strong {
  color: var(--bgg) !important;
}

/* correct words */
.correct {
  border-bottom: 3px solid var(--bgg) !important;
}

/* wrong */
.wrong,
#wrong .value strong {
  color: var(--bgr) !important;
}

/* wrong words */
.wrong {
  border-bottom: 3px solid var(--bgr) !important;
}

/* highlighted wrong words */
.highlight-wrong {
  border-radius: 10px !important;
  color: var(--bgo) !important;
  background-color: var(--bgr) !important;
}

/* highlight on current word */
.highlight {
  background-color: var(--bgt) !important;
  background-color: transparent !important;
  color: var(--bgo) !important;
  border-bottom: 3px solid var(--bgt) !important;
}

/* blue buttons and such */
#timer,
#reload-btn,
.btn-primary {
  border-radius: 12px !important;
  background-color: var(--bgd) !important;
}

/* green buttons */
.btn-success {
  border-radius: 12px !important;
  background-color: var(--bgg) !important;
}

.btn {
  border-radius: 12px !important;
}

/* body fixes */
body {
  /* overflow: hidden !important; */
  background: var(--bgn) !important;
  min-height: 100vh !important;
}

/* body background */
body::before {
  content: "" !important;
  position: fixed !important;
  top: 0 !important;
  margin: -40px !important;
  height: calc(100% + 80px) !important;
  width: calc(100% + 80px) !important;
  z-index: 0 !important;
  background-color: var(--bgn) !important;
  background-size: cover !important;
  background-position: center !important;
  /*background-image: url('http://goo.gl/jV3MM9') !important; /**/
}

/* top navigation */
.top-first-bg {
  z-index: 99 !important;
  top: 0 !important;
  left: 0 !important;
  right: 0 !important;
  position: fixed !important;
  height: 85px !important;
  margin-top: -48px !important;
  background: var(--bgd) !important;
  box-shadow: 0px 10px 210px rgba(0, 0, 0, 0.082) !important;
  transition: ease-in-out 200ms !important;
}

/* hover */
.top-first-bg:hover {
  margin-top: 0px !important;
  transition-delay: 1000ms !important;
}

/* top navigation logo */
.navbar-brand {
  /* display: none !important; */
}

/* side navigation */
#sidebar-md-lg {
  position: fixed !important;
  top: 30px !important;
  left: 0 !important;
  bottom: 0 !important;
  width: 340px !important;
  margin-left: -340px !important;
  border-radius: 0 !important;
  background-color: var(--bgd) !important;
  transition: ease-in-out 450ms !important;
  z-index: 98 !important;
}

/* hover area */
#sidebar-md-lg:after {
  position: absolute !important;
  top: 0 !important;
  right: -10vw !important;
  bottom: 0 !important;
  width: 10vw !important;
  content: "" !important;
  background-color: rgba(255, 255, 255, 0) !important;
}

/* hover */
#sidebar-md-lg:hover {
  margin-left: 0px !important;
  box-shadow: 10px 10px 60px rgba(0, 0, 0, 0.082) !important;
}

/* sidebar items */
.list-group-item {
  margin-bottom: 10px !important;
  border-radius: 20px !important;
  background-color: var(--bgh) !important;
  color: var(--bgt) !important;
  transition: 300ms ease-in-out !important;
}

#sidebar-md-lg > div > a {
  border-radius: 10px !important;
}

/* hover */
.list-group-item:hover {
  background-color: var(--bgd) !important;
  color: var(--bgt) !important;
}

/* responsive */
@media (min-width: 0px) and (max-width: 1000px) {
  #sidebar-md-lg {
    display: block !important;
  }
  #sidebar-md-lg:after {
    right: -5vw !important;
    width: 5vw !important;
    background-color: rgba(255, 255, 255, 0) !important;
  }
}

/* main content */
#main {
  width: 100% !important;
  max-width: 1140px !important;
  margin: 0 auto !important;
  float: none !important;
}

/* children */
.row.main-layout {
  padding: 0 !important;
  margin-top: 10px !important;
  /*box-shadow: 0 0 12px 0 rgba(0,0,0,0.1) !important; */
}

.main-layout #main-content,
.main-layout .main-content {
  border-radius: 20px !important;
}

/* speedtest container */
#speedtest-main {
  width: 100% !important;
  /* took me 30 minutes to resolve this..... */
  padding: 0 !important;
}

/* login/language */
#speedtest-main > .row {
  width: 820px !important;
  margin: 20px auto 0 !important;
}

.col-md-6.col-sm-8.col-xs-12 > div.btn-group > a.btn.btn-primary {
  padding: 7px 14px !important;
}

.col-md-6.col-sm-8.col-xs-12 > div.btn-group {
  padding-right: 5px !important;
}

/* child element */
#speedtest-main > .row > div {
  padding: 0 !important;
}

/* button fix */
#speedtest-main > .row > div > .btn-group {
  margin: 0 !important;
}

/* language list */
#language-list {
  border-radius: 20px !important;
  padding: 10px !important;
  display: none;
  background: var(--bgh) !important;
  margin: 0 0 20px 0 !important;
}

#language-list h4 {
  border-radius: 20px !important;
}

/* switch language */
#language-list > h4 {
  text-align: center !important;
  background: var(--bgd) !important;
}

/* switch language */
#language-list > a {
  text-align: center !important;
  transition: 300ms ease-in-out !important;
}

/* hover over the elements */
#language-list > a:hover {
  text-align: center !important;
  background: var(--bgd) !important;
  border-radius: 10px !important;
}

/* speedtest box */
#reload-box {
  max-width: 820px !important;
  margin: 0 auto !important;
}

/* child elements */
#reload-box > div {
  /* width: 100% !important; */ /* ------------------------------------------------------------------------------------------------------------------------- */
  margin: 0 auto !important;
}

/* words */
#words {
  border-radius: 20px 20px 0px 0px !important;
  text-align: center !important;
  padding: 6px 6px 30px 6px !important;
  margin-bottom: 0 !important;
  background-color: var(--bgh) !important;
}

/* words row */
#row1 {
  color: var(--bgt) !important;
}

/* input row */
#input-row {
  border-radius: 0px 0px 20px 20px !important;
  background-color: var(--bgh) !important;
}

/* child */
#input-row > div {
  max-width: calc(100% - 130px) !important;
}

/* input bar */
#inputfield {
  width: calc(100% - 130px) !important;
  margin-left: 130px !important;
  text-align: center !important;
  border-bottom: 3px solid rgba(0, 0, 0, 0.1) !important;
