css:
@-moz-document domain(openair.com) {
	select.timesheetControlPopupCustomerProject, 
    select.timesheetControlPopup {
		width: 300px;
	}
       table .displaylist {
         color: #09c;
       }.timesheetControlPopupCustomerProject, .timesheetControlPopup {
    width: 300px;
}
.ts_approve_reject {
    padding:  0.5rem;
}
.displayList {
	padding: 0  0.5rem;
}
.displayList td[align=right]  {
 	text-align: left;
    
}

tr[valign=top]{
	color: #000;
}
tr[valign=top] td:not([colspan='19']) table {
	color: #00c8ff;
}

td[align=right] > .ts_approve_reject  {
    width: 20rem;
    display: block;
    margin: 1rem 0;
}

td[align=right] > .ts_approve_reject > label  {
	padding: 8px ;
    padding-top: 8px ;
    cursor: pointer;  
    margin-right: 10px;
    border: 1px solid  #00c8ff;
    background: white;
    border-radius: 999rem;
}
td[align=right] > .ts_approve_reject + div > input[type=text] {
	padding: 0.5rem;
}
.bodyIndent > .noBorder > tbody > tr:nth-child(4) > td:nth-child(2) > table > tbody > tr > td {
	background:  #00c8ff;
    font-size: 1.5rem;
    font-weight: bold;
}
.bodyIndent > .noBorder > tbody > tr:nth-child(4) > td:nth-child(2) > table > tbody > tr > td > b {
	  font-size: 1rem;
}
.oa3_ui input[type="button"]:not(.ui3extended) {
    margin-bottom: 1rem!important;
	margin-left: 0!important;
    padding: 1rem;
}
}
