}

.dark-theme .input-group-btn .input-dropdown-btn {
    border-color: hsla(0, 0%, 100%, .7);
}

.dark-theme .catalog-container .search-container .input-group .input-group-btn .input-dropdown-btn .rbx-selection-label,
.dark-theme .rbx-selection-label {
    color: hsla(0, 0%, 100%, .7) !important;
}

.dark-theme .category-section {
    background-color: rgb(52, 54, 56) !important;
}

.dark-theme .text-link-secondary {
    color: #bdbebe !important;
}

.dark-theme .text-link-secondary:hover {
    color: white !important;
}


.dark-theme .ng-binding[ng-bind="'Label.Card.PriceWas' | translate"],
.dark-theme .strike-through {
    color: #00b06f !important;
}
}

