@-moz-document domain(eksisozluk.com) {
/* Ekşi Sözlük Compact Dark eksisozluk.com */
/* v1.1 */
/* https://atakanau.blogspot.com */

/* background */
body
,body > header
,body.theme-enabled > header
,.report-link:hover
,#search-form #search-textbox
,footer
,.pager > select
,#index-section:hover
,#top-bar input, #top-bar select
{background:#111;}

#top-bar
, ul.toggles-menu>li
,#entry-item-list > li
,html.no-touch .pager > a:hover
,html.no-touch a.more-data:hover
{background:#050505;}

#search-form button[type="submit"]
{background:#344D1F}

.ui-autocomplete
,#top-bar > #advanced-search-form
{background:#5b1b01}

.no-touch .ui-autocomplete > li > div.ui-state-active
/*, .no-touch .ui-autocomplete > li > div:hover*/
{background:#632}


html.no-touch div>nav>ul.topic-list > li > a:hover
{background:#010;}

.related-titles {background-color:initial;}

/* color */
body
,.theme-enabled #top-navigation > ul > li > a
,#top-bar input, #top-bar select
,.ui-autocomplete > li > div
,.no-touch .ui-autocomplete > li > div.ui-state-active, .no-touch .ui-autocomplete > li > div:hover
,#top-bar > #advanced-search-form
,h1, h2, h3, h4, h5, h6
{color:#cba}

a
,.topic-list li > a
,.pager > select
,#entry-item-list div.info > a.entry-author
,#entry-item-list a
{color:#795}

#entry-item-list .feedback a svg
,#entry-item-list footer .eksico
{fill:#000;}
.channel-filter-toggle .eksico
{fill:#795;}

#matter-index-item-list a, #matter-content a, #draft-content a, #disambiguations a, #pinned-entry a
{color:#9f3}

.topic-list.partial > li > a > small
,#entry-item-list footer .permalink
{color:#876}

#entry-item-list footer .other .eksico
{fill:#876;}

/* border */
#search-form #search-textbox
,.pager select,.pager a
,#entry-item-list footer .other .dropdown-menu
{border: 1px solid #344D1F}

.ui-autocomplete::after{border-bottom-color:#5b1b01}

.topic-list.partial > li:not([id]):nth-child(even)
{border-bottom: 1px #222 solid;}

.topic-list.partial > li:not([id]):nth-child(odd)
{border-bottom: 1px #000 solid;}

body > header
,.ui-autocomplete,.ui-autocomplete::before
,#top-bar > #advanced-search-form
,#top-bar input, #top-bar select
{border-color: #000;}

html.no-touch #sub-navigation > ul > li:not(.no-hover).active > a
{border-bottom-color:#344D1F}

#site-footer
,.more-data
{border-color:#222;}

/* pad margin */
#content-body
{margin: 0 0 0 .5em;}

#entry-item-list, #matter-index-item-list, #matter-content, #pinned-entry, #matter-answer-index-item-list, #matter-answer-list
{margin-top: 0;margin-bottom: 0;}

.topic-list.partial > li > a
{padding: .2em 30px .1em 0;}

#entry-item-list > li
, #matter-index-item-list > li
, #matter-content > li
, #pinned-entry > li, #matter-answer-index-item-list > li
, #matter-answer-list > li
{padding: 0 .5em;}

#sub-navigation > ul > li > a
{margin: 0;padding: 0;}

/* custom */

#logo a{background-image: url('https://svgshare.com/i/YtL.svg');}

#aside{float: initial;}
.topic-list li > a
{color:#795}

#topic > h1+ul
{margin-bottom: 0 !important;
border-bottom: 1px #000 solid;}

li>footer
{left:-2%;width:104%;position:inherit;}

.topic-list.partial > li > a{white-space: nowrap;overflow: hidden;text-overflow: ellipsis;}

/* position */
.topic-list.partial > li > a > small
{top:0}

/* size */
#index-section
{width: 350px;}
#index-section:hover
{width: 390px;}
#main
{margin-left: 350px;}
#content-body{width: 99%;}

html > body > header #top-bar{height: 80px;}
#search-form{margin-top: 5px;}
#top-navigation,#top-navigation > ul {height: 40px;line-height: 40px;}
html.no-touch > body{padding-top: 51px;}
html.no-touch #index-section{top: 81px;}

.content br + br{display: block;margin: .2em;}

/* mobile */
@media handheld, only screen and (max-width: 850px) {

    /* size */
    #main
    {margin-left: 0;}

    #content-body
    {width: 98%;}
    
    /* border */
    #top-bar{border-bottom-color:#000;}

}

/* hide */
#aside
,.share-links
,#sub-navigation > #nav-extra-buttons
,#cookie-info-container
,#index-filter-prefs
,.under-top-ad
,.ui-menu-item[style]   /* sponsorlu arama otomatik tamamlama */
,.ui-menu-item>div[style]   /* sponsorlu arama otomatik tamamlama */
{display:none;height:0;overflow:hidden;}

/* entry footer */
.info{
  position: relative;
  top: -2.5em;
  right: 5em;
}

div#entry-nick-container,
div#entry-nick-container + div{
	display:inline-block !important;
}

ul li footer {
 height: 37px;
}
.rate-options {
  margin-left: 1em;
}
/* entry footer. */

/* tooltip */
.entry-footer-bottom {
	display: inline-block;
	color: #555;
	cursor: help;
	position: relative;
	-webkit-transform: translateZ(0);
	/* webkit flicker fix */
	-webkit-font-smoothing: antialiased;
	/* webkit text rendering fix */
	z-index: 999993;
}
.entry-footer-bottom .avatar-container {
	background: #0b0b0b;
	bottom: 100%;
	display: block;
	left: 10px;
	margin-bottom: 5px;
	opacity: 0;
	padding: 5px;
	pointer-events: none;
	position: absolute;
	width: 50px;
	-webkit-transform: translateY(10px);
	-moz-transform: translateY(10px);
	-ms-transform: translateY(10px);
	-o-transform: translateY(10px);
	transform: translateY(10px);
	-webkit-transition: all .25s ease-out;
	-moz-transition: all .25s ease-out;
	-ms-transition: all .25s ease-out;
	-o-transition: all .25s ease-out;
	transition: all .25s ease-out;
	-webkit-box-shadow: 2px 2px 6px rgba(0, 0, 0, 1);
	-moz-box-shadow: 2px 2px 6px rgba(0, 0, 0, 1);
	-ms-box-shadow: 2px 2px 6px rgba(0, 0, 0, 1);
	-o-box-shadow: 2px 2px 6px rgba(0, 0, 0, 1);
	box-shadow: 2px 2px 10px rgba(0, 0, 0, 1);
}
.entry-footer-bottom .avatar-container:before {
	bottom: -20px;
	content: " ";
	display: block;
	height: 20px;
	left: 0;
	position: absolute;
	width: 100%;
}
.entry-footer-bottom .avatar-container:after {
	border-left: solid transparent 10px;
	border-right: solid transparent 10px;
	border-top: solid #0b0b0b 10px;
	bottom: -10px;
	content: " ";
	height: 0;
	left: 55%;
	margin-left: -13px;
	position: absolute;
	width: 0;
}
.entry-footer-bottom:hover .avatar-container {
	opacity: 1;
	pointer-events: auto;
	-webkit-transform: translateY(0px);
	-moz-transform: translateY(0px);
	-ms-transform: translateY(0px);
	-o-transform: translateY(0px);
	transform: translateY(0px);
}
/* tooltip. */
}
