@namespace url(http://www.w3.org/1999/xhtml);
/*
caramel*vanilla
http://caramel-tea.com/
*/

@-moz-document domain("caramel-tea.com") {
/* template v4 beta update 2008/04/14 r2 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
html {
font-size : 100% !important;
background-color : #dcdcd4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #dcdcd4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#doc
, #doc div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#doc * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#doc a:link { color : #0000ee !important; }
#doc a:visited { color : #551a8b !important; }
#doc a:hover { color : #ee0000 !important; }
#doc a:hover { background-color : transparent !important; }
/* /destroy div layout */

#subcontent
, h2
, #about
, .avatar
, .UTWPrimaryTags
, .UTWPrimaryTags + br
, #searchform
{ display : none !important; }

#doc #recent h3
{ border : 0 !important; }

#doc #recent .link-list a
, #doc #recent h3
, #doc .date
{ padding : 0 !important; }

#doc input
{ border : 1px solid #777 !important; }

}
