css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cococomic.com") {



#Dc1, #Dc2, #Dc3, #Dc4, #Dc5, #Dc6, #Dc7, #ad9_temp, #_u114qq, #Dc1 + table{
display:none;
}

#Cc {
position:absolute;
top:0;
left:auto;
right:auto;
display:block;
}

#Dc1 + table + table {
background: #fdfdfd;
}

#Dc3 + table {
position:fixed;
bottom:0;
right:0;
width:100% !important;
height:100% !important;

}

#Dc3 + table td {
vertical-align: bottom !important;
}

.input_top {
height:auto !important;
opacity: 0.5 !important;
}

.input_top:hover {
height:auto !important;
opacity: 1 !important;
}

.input_top {
position:fixed;
display:block;
top:50%;

background-color:#FFF !important;
border-color:#DDD !important;
padding:20px !important;
}

.input_top + .input_top {
top:0;
padding:0 !important;
}

.input_top + .input_top + .input_top{
display:block;
top:50%;
right:0;
padding:20px !important;
}

}
