css:
@-moz-document domain('zhihu.com'), domain('zhuanlan.zhihu.com') {
    body, 
    .Topstory-tabCard, 
    .TopstoryItem, 
    .ContentItem-actions,
    .CommentsV2-withPagination,
    .CommentTopbar,
    .CommentListV2-header-divider,
    .HotList,
    .HotItem,
    .QuestionHeader,
    .QuestionHeader-footer,
    .QuestionAnswer-content,
    .Card,
    .CommentItemV2,
    .NestComment,
    .CommentsV2-footer,
    .QuestionPage,
    .css-qniote-Header,
    .Post-Main,
    .Post-content,
    .Recommendations-Main,
    .App-main {
        background: #333;
        color: #ccc; 
    }
    .PostItem {
        background: #444;
    }
    .PostItem-Title,
    .BlockTitle {
        color: #aaa;
    }
	.CommentTopbar-title,
    .PostItem-Summary {
        color: #888;
    }
    .QuestionMainAction,
    .KfeCollection-IntroCard-newStyle-pc {
        background: #444;
    }
    .CommentsV2-openComment-divider,
	.CommentsV2-openComment-divider + div ,
	.CornerButton,
	.css-jpzy4w,
	.css-rtxt89,
	.CommentCollapseButton {
		background: #2c2c2c;
    }
	.CommentEditorV2-inputWrap,
	.CommentEditorV2-input,
	.css-17oyyq4 {
        background: #3c3c3c;                           
    }    
    .CommentEditorV2-inputWrap {
        border-color: #4f4f4f;
    }
    header, 
    .GlobalSideBar,
    .Question-sideColumn,
    .List-header,
    .QuestionHeader-side,
    .ColumnPageHeader,
    .Post-SideActions,
    .AppHeader 
    .Recommendations-BlockTitle
	.CommentItemV2:after {
        display: none;
    }

    .ContentItem-title,
    .RichContent-inner,
    .HotItem-title,
    .HotItem-excerpt,
    .QuestionHeader-title {
        color: #aaa;
    }

    .Topstory-mainColumnCard .Card:not(.Topstory-tabCard),
    .Topstory-tabs,
    .Topbar,
    .CommentsV2-withPagination,
    .NestComment:not(:last-child):after,
    .NestComment--rootComment:after,
    .NestComment .NestComment--child:after,
    .Topstory--old .HotItem:not(:first-child),
    .Topstory--old .HotListNav,
    .List-item+.List-item:after,
    .List-header,
	.HotItem:not(:first-child),
    .CommentsV2-footer {
        border: none;
    }

    .Topstory-mainColumn,
    .QuestionHeader-main,
    .Question-mainColumn {
        width: 100%;
    }

    .Post-NormalMain .Post-Header, .Post-NormalMain>div, .Post-NormalSub>div {
        width: 80%;
    }
}
