css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#wmn-statusbar, #wmn-toolbar-button
{
list-style-image: url("data:image/png;base64,") !important;
}

toolbar #wmn-toolbar-button .wmn-toolbar-value
{
 text-align: left !important;
 font-size: 8px !important;
 font-weight: bold !important;
}

toolbar:not([iconsize="small"]) #wmn-toolbar-button .wmn-toolbar-value
{
 margin-top: -5px !important;
 margin-left: -3px !important;
}

toolbar[iconsize="small"] #wmn-toolbar-button .wmn-toolbar-value
{
 margin-top: -3px !important;
 margin-left: -2px !important;
}
