css:
/*Amazon Prime Video Transparent
by Monkeydj
v 0.1.2021
*/
@-moz-document domain("www.primevideo.com") {
 .atvwebplayersdk-title-text {
    opacity:.45;
}
.atvwebplayersdk-title-text:hover {
    opacity:1;
}
.atvwebplayersdk-subtitle-text {
    opacity:.24;
}
.atvwebplayersdk-subtitle-text:hover {
    opacity:1;
}
.collapsibleXrayHeader, .xrayQuickViewListItem, .atvwebplayersdk-bottompanel-container, .atvwebplayersdk-playerclose-button, .atvwebplayersdk-hideabletopbuttons-container button {
    opacity:.3;
}
.xrayQuickViewListItem:hover, .atvwebplayersdk-playerclose-button:hover, .atvwebplayersdk-hideabletopbuttons-container button:hover {
    opacity: 1;
}
.atvwebplayersdk-bottompanel-container:hover {
    opacity:.85;
}
.atvwebplayersdk-timeindicator-text {
    font-size:1.23rem;
}
.atvwebplayersdk-trickplay-time {
    font-size:1.23rem;
}
}
