css:
@-moz-document domain(dailykos.com) {

html {
padding: 0 !important;
margin: 0 !important;
}

body {
background: none !important;
background-color: #eee !important;
color: #242424; 
padding: 0 !important;
margin: 0 !important;
line-height: 1.4em !important;
}

body,
td,
span.busyBall,
.cbar,
#comments,
#notifier,
#cHelp,
#recommenders,
#tagEditor,
#diaryGuide,
#pollGuide {
  font: 11pt Verdana, Geneva, Arial, Helvetica, sans-serif !important;
}

body > div:first-child {
display: none !important;
}

* html body {
  text-align: center;
}
* html #container {
  text-align: left;
}

h1 {
margin: auto !important;
text-align: center !important;
padding: 0 !important;
margin: 0 !important;
background-color: rgb(238,125,25) !important;
height: 123px !important;
}

h1 a, h1 a span {
padding: 0 !important;
margin: 0 !important;
}

h1 a span img 	{
display: none !important;
}

