#ads, #adblocker {
display: none !important;
}
	
#container {
width: 90% !important;
height: auto !important; 
padding: 0 !important;
margin: auto !important;
background: white !important;
border: none !important;
/*
border-left: 1px dotted black !important;
border-right: 1px dotted black !important;
*/
}


#main,
#comments,
#e {
width: inherit !important;
}

	
#main	{

line-height: 135% !important;
float: left !important;
width: 80% !important;
margin: 0 !important;
padding: 0 !important;
border: none !important;
background-color: white !important;
/*
border-right: 1px dotted black !important;
margin-right: -1px !important;

font-size: 12px;
z-index: 51;
line-height: 135%
*/
}

#main > * {

padding: 0 10px !important;

}



#main table {background:#FFF}

#main .entry {margin-bottom: 2em;}

#main h1 {margin-bottom:1em}


#main h2 {font-size: 1.34em; padding-bottom:3px; line-height:1.5em;}

#main h2 a {font-size:1.1em;line-height:0.9em}


#main h3.byline, #main h4.date {font-weight: normal;}

#main h3.byline {margin-bottom:0.5em}


#main h3.byline a {}
#main h4.date {font-style: italic; margin-bottom: 1em;}
#main ul.catcom {margin: 1em 0 0; padding: 0;}
#main ul.catcom li {display: inline; list-style: none;}


#main p {line-height:1.5em}



h2 {
font-size: 12pt !important;
}

h3 {
font-size: 11pt !important;
}

h4 {
font-size: 10pt !important;
}

p, li, label {
font-size: 10pt !important;
}

a {

font-weight: 500 !important;
}

object {
display: block !important;
margin: auto !important;
}

#footer {
border: none !important;
clear: both; text-align: center; background: #FFF; padding: 1em 0; width: inherit !important; margin: 0 auto;
/*
border-top: 1px dotted #999; _border-top: 1px solid #ccc
*/
}




#pageWidth {margin: 0 0 10px}

#pageWide,
#pageNarrow {background: 100% 0 no-repeat; padding: 2px 0 7px}

#pageWide {background-image: url(http://images.dailykos.com/images/admin/wideOn.gif); padding-right: 46px; color: #F50}
#pageNarrow {background-image: url(http://images.dailykos.com/images/admin/narrowOn.gif); padding-left: 4px; padding-right: 38px; color: #F50}

