}

#sidebar h3 {
background-color: rgb(238,125,25) !important;
}

#sidebar ul {
margin: 0 !important;
padding: 0 0 0.5em !important;
}

#sidebar li {
padding: 0.3em 0 !important;
}

a:link {
  color: #FC8F19;
  text-decoration: none; 
  font-weight: bold;
}
a:visited {
  color: #D46317; 
  text-decoration: none; 
  font-weight: bold;
}
a:hover {
  color: #000;
}
a img {
  border: 0;
}
a.dk {
  font-size: 10px; 
  text-decoration: none; 
  font-weight: bold; 
  border-bottom: #333; 
  border-width: 0 0 1px 0; 
  border-style: none none dotted none;
} 
a:link.dk {
  color: #F60;
} 
a:visited.dk {
  color: #BD4D02;
} 
a:hover.dk {
  color: #BD4D02; text-decoration: underline;
}

h1,
h2,
h3,
h4,
h5 {
  margin: 0; 
  padding: 0; 
  font-size: 1em;
}

p {
  margin: 0.5em 0 0.54em;
}

p, br {
  line-height:1.5;
}

blockquote,
.blockquote {
  color: #222; 
  border: 1px #FC0 dotted; 
  background: #F0F0F0; 
  padding: 10px 20px 10px 20px; 
  margin: 10px 25px 10px 25px;
}

blockquote > blockquote {
  background: #FFF;
}
blockquote > blockquote > blockquote {
  background: #F0F0F0;
}

.indent {
  padding:0 20px; 
  margin:5px 25px;
}

.border {
  border: 1px solid #000;
}

center {
  text-align: center;
}

#sidebar h2 {display: none;}
#sidebar h3 {background: #EE7B19; color: #fff; padding: 0.25em 10px 0.15em 30px; font-size: 1.1em; line-height:1.5em; font-weight: normal;}
#sidebar h4, #sidebar p {margin-left: 10px; margin-right: 10px;}
#sidebar h4 {font-weight: normal; margin-top: 0.666em;}
#sidebar ul {margin: 0; padding: 0 0 0.5em; list-style: none;}
#sidebar li {padding: 0.2em 10px 0 0;}
#sidebar li a {padding-left: 30px; background: url(http://images.dailykos.com/images/admin/star_orange.gif) 10px 1px no-repeat;}
#sidebar li:active a,#sidebar li a:hover,#sidebar li form:hover {background-image:url(http://images.dailykos.com/images/admin/star_black.gif);color:#000}

#sidebar li form {margin:0;background:url(http://images.dailykos.com/images/admin/star_orange.gif) 10px 5px no-repeat;padding-left:30px}


#sidebar form {margin: 0 0 1em;}
#sidebar .text {width: 120px;}
#sidebar form p {margin-top: 0.5em; margin-bottom: 0.5em;}


#sidebar #switcher {margin: 0; padding: 10px 0 5px 0;
  height: 30px;}
#switcher li {float: left; padding: 0; margin: 0 0 0 13px;
  background: 0 0 no-repeat;
  width: 79px; height: 24px;}
#switcher li a, #switcher li a:hover {display: block;
  width: 79px; height: 24px; padding: 0;}
#switcher li#narrow {background-image: url(http://images.dailykos.com/images/admin/n_off.gif);}
#switcher li#wide {background-image: url(http://images.dailykos.com/images/admin/w_on.gif);}

#switcher li#wide a {
  visibility: hidden;
}

span.busyBall {margin:0;padding:0 14px 0 4px;font-style:italic}
span.busyBall span.bbi {position:absolute;overflow:hidden;height:16px;width:16px;margin:0;padding:4px 0 0}
span.busyBall > span.bbi {padding:1px 0 0}
span.busyBall span.bbc {padding:0 0 0 20px}

#sidebar span.busyBall {padding-left:0}
#sidebar span.busyBall span.bbi {padding:1px 0 0}


#navlist {padding:3px 0;margin-left:0;border-bottom:1px solid #778;font:bold 12px Verdana,sans-serif}

#navlist li {list-style: none; margin: 0; display: inline;}
#navlist li a {padding: 3px 0.5em; margin-left: 3px; border: 1px solid #444; border-bottom: none; background: #CF9; text-decoration: none;}
#navlist li a:link { color: #444; }
#navlist li a:visited { color: #555; }
#navlist li a:hover {color: #000; background: #FC6; border-color: #444;}
#navlist li a#current {background: white; border-bottom: 1px solid white;}


/* polls: use 440px for width in wide front page stylesheets, 354px for narrow */

#poll {margin:0 45px 20px}


.poll {width:440px;margin:20px auto;text-align:left}
.poll ul {margin-left:0;padding-left:0}
.poll ul li {list-style:none;margin-left:0}
.poll ul li label {z-index:20}
.poll table {border-collapse:separate;border-spacing:0;text-align:left;width:100%}
.poll td {text-align:left;margin:0}
.poll tr.po td {padding:4px 4px 0 0}
.poll td.pl,.poll tr.po {width:100%}
.poll tr.pr td {padding:0 10px 4px 0;border-bottom:1px solid #f4f4f4}
.poll .vote tr.pr td {display:none}
.voted .pvi,.voted .pvr,.vote .pvv,.vote #pollGuide,.voted #pollGuide {display:none}
.poll tr.pr td.pt {padding-left:2px;background:url(http://images.dailykos.com/images/admin/Thermometer17.gif) no-repeat}
.pollQuery,.vtotal,.vct {font-weight:bold}
.poll td.pl input {display:none}
.poll .voted td input {display:none}
.poll .vote tr.po td img {display:none}
.poll .voted td.po img {display:inline}
.pollControls {text-align:center}
.pollControls input {padding:1px 10px;font-size:12px;vertical-align:middle}
.poll fieldset {text-align:inherit;margin:0;padding:8px;border:1px solid #FC8F19}
.poll legend {padding:0 6px;color:#242424;font-size:1.4em;font-weight:bold}

.poll .voteform tr.pr td {display:none}

.poll .voteform td.pl input {display:inline;font-size:11px;width:394px}

.poll .voteform td.pl label {display:none}

#pollQueryInput {display:none}
.voteform .pollControls,.voteform .pollQuery {display:none}
.voteform #pollQueryInput {display:block}
#pollQueryInput label {font-size:12px}
#pollQueryInput input {vertical-align:middle;font-size:12px;font-weight:bold;padding:2px 0;width:350px}
.pollControls {text-align:center}
.pollControls input {padding:1px 10px;font-size:12px;vertical-align:middle}
#addPollP, #rmvPollP {display:none;text-align:center;padding-bottom:8px}
.poll .add #addPollP, .poll .voteform #rmvPollP {display:block}
.published .poll .add,.poll .add .polltb,.poll .add .pollControls,.poll .add #pollGuide {display:none}



#e dt,
#ie dt,
#be dt {width:100px}

div.cx,
div.cx h3,
div.cx h3 a,
div.cx h3 span /*header*/ {
font-size: 10pt !important;
}

div.crs dl, /*rec*/
div.ct p,   /*comment*/
div.cf p    /* sig */ {
font-size: 9pt !important;
}

.cbar {
width: inherit !important;
}

.entry {
/*width: 100% !important;*/
}


}
