css:
@-moz-document domain("www.youtube.com")
{
/* list of variables to stop me going completely insane.

backimgcol-rgb  -- backimgtrans
borcol-rgb  -  bortrans 
primfontcol-rgb	-  fontrans
secfontcol-rgb - secfonttrans
hovtextcol-rgb - hovtexttrans
hovbackcolor-rgb -  hovbacktrans
semibackcol-rgb  -  semibacktrans  
boxshadowcol -rgb     -   boxshadowtrans
buttoncolor-rgb
buttoncolorhover-rgb
backgroundimage
hoverofftimer
likebarcolour-rgb
dislikebarcolour-rgb

255, 255, 255
  
  
---xyz---  if object is in background image class because it cannot be made transparent
these current include channelpage,

breakclash:  miight clash with something else and break it
multifix:   affects a number of objects in an unexpected way, both good and bad depending on case
bug: - bugs!



*/





/*  ---------------------------------------------------------------------------------- */
/* front page */
/* #contentaffects a lot of things */


  #guide-content.ytd-app, /* left side category*/
ytd-app /* front page back */
  {
    background-image: linear-gradient(
  rgba(0, 0, 0,0.3),
  rgba(0, 0, 0,0.3)
         ),                
                                            
  url(https://i.postimg.cc/B6qFqCkp/1.jpg) !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
    background-size: cover !important;
  }

  ytd-masthead /*search box */
,ytd-mini-guide-renderer.ytd-app, ytd-mini-guide-entry-renderer/*half screen trend home sub etc*/
  {
    background: transparent !important;
  }
/*text*/
/*text primary */
  ytd-rich-grid-video-renderer[mini-mode] #video-title.ytd-rich-grid-video-renderer,#video-title.ytd-rich-grid-video-renderer /* full and half view video title*/
,.title.ytd-guide-entry-renderer /*left side category */
,#guide-section-title.ytd-guide-section-renderer /*more from youtube etc heading */
,#title.ytd-rich-grid-renderer /* recommeneded video header */
,#title.ytd-rich-shelf-renderer/* top news, latest etc headers */
  {
    color: rgba(255, 255, 255,1) !important;
  }

/*text secondary */
  #guide-links-primary.ytd-guide-renderer > a, #guide-links-secondary.ytd-guide-renderer > a /* about privacy etc left side*/
,#footer.ytd-guide-renderer > #copyright /*copyright statement text */
,ytd-video-meta-block[rich-meta] #channel-name.ytd-video-meta-block, ytd-video-meta-block[rich-meta] #metadata-line.ytd-video-meta-block, yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string /* video views, channel name etc thumbnail*/
,yt-formatted-string.ytd-guide-signin-promo-renderer/*sign in to like video etc statement */
  {
    color: rgba(250, 250, 250,1) !important;
  }


/*hover*/
  ytd-guide-entry-renderer:hover /*left side category */
,ytd-rich-item-renderer:hover /* video thumbnail*/
,#content.ytd-rich-item-renderer ytd-post-renderer.ytd-rich-item-renderer:hover /*latest youtube posts */
  {
    background-color: rgba(245, 245, 245,0) !important;
    box-shadow: inset 0px 0px 10px 3px rgba(250, 250, 250,0
) !important;
    transition: none !important;
  }
/*hover off*/
  ytd-guide-entry-renderer /*left side category */
,ytd-rich-item-renderer /* video thumbnail*/
,#content.ytd-rich-item-renderer ytd-post-renderer.ytd-rich-item-renderer /*latest youtube posts */
  {
    background-color: rgba(0,0,0,0) !important;
    box-shadow: none !important;
    transition: all 1s !important;
  }






/*  ---------------------------------------------------------------------------------- */
/* video page */

/*text*/
/*primary*/
  ytd-compact-video-renderer #video-title.ytd-compact-video-renderer/*video title thumbnail */
,.title.ytd-video-primary-info-renderer /* video title */
,.content.ytd-video-secondary-info-renderer /* video description text */
,#content-text.ytd-comment-renderer /* comment text*/
,ytd-compact-radio-renderer.use-ellipsis #video-title.ytd-compact-radio-renderer/* mix playist video title text */
,#icon-label.yt-dropdown-menu/* comment sort by */
,#label.ytd-pinned-comment-badge-renderer/*pinned by*/
,#contenteditable-root.yt-formatted-string/* user comment text box*/
,#button.ytd-button-renderer yt-icon.ytd-button-renderer + yt-formatted-string.ytd-button-renderer/*comment reply */
,#autoplay.ytd-compact-autoplay-renderer/* auto play */
,ytd-post-renderer[uses-compact-lockup_][attachment=image] #home-content-text.ytd-post-renderer, ytd-post-renderer[uses-compact-lockup_][attachment=none] #home-content-text.ytd-post-renderer,ytd-post-renderer[uses-compact-lockup_] #author-text.yt-simple-endpoint.ytd-post-renderer/*latest youtube posts */
,#upnext.ytd-compact-autoplay-renderer/* up next */
  {
    color: rgba(255, 255, 255,1) !important;
  }

  #simplebox-placeholder.ytd-comment-simplebox-renderer /* add public comment active*/
  {
    color: rgb(255, 255, 255)!important;
  }

/*text*/
/*secondary*/
  #container.ytd-channel-name ,#metadata-line.ytd-video-meta-block span.ytd-video-meta-block /*video  thumbnail subtitle */
,span.yt-view-count-renderer,#date.ytd-video-primary-info-renderer/*video views and date */
,#owner-sub-count.ytd-video-owner-renderer/*subscribed*/
,ytd-toggle-button-renderer.style-text[is-icon-button] #text.ytd-toggle-button-renderer
,ytd-button-renderer.style-text[is-icon-button] #text.ytd-button-renderer, ytd-button-renderer.style-default[is-icon-button] #text.ytd-button-renderer, ytd-button-renderer.style-opacity[is-icon-button] #text.ytd-button-renderer /*likes dislikes, share save */
,#vote-count-middle.ytd-comment-action-buttons-renderer/*comment likes  */
,#author-text.yt-simple-endpoint.ytd-comment-renderer /* comment name */
,paper-button.ytd-subscribe-button-renderer[subscribed] yt-formatted-string.ytd-subscribe-button-renderer/*subcribed button active */
,.count-text.ytd-comments-header-renderer/* number comments*/
,ytd-button-renderer yt-formatted-string.ytd-button-renderer /*comment reply*/
  {
    color: rgba(250, 250, 250,1) !important;
  }   


/* hover on*/
  #dismissable.ytd-compact-video-renderer:hover
,paper-button.ytd-subscribe-button-renderer[subscribed]:hover /*subcribe */
,paper-button.ytd-subscribe-button-renderer/* subcribe unactive*/
  {
    background-color: rgba(245, 245, 245,0) !important;
    box-shadow: inset 0px 0px 10px 3px rgba(250, 250, 250,0
) !important;
    transition: none !important;
  }


/*hover off*/
  #dismissable.ytd-compact-video-renderer
,paper-button.ytd-subscribe-button-renderer[subscribed] /*subcribe */
  {
    background-color: rgba(0,0,0,0) !important;
    box-shadow: none !important;
    transition: all 1s !important;
  }


/*  playlist */
  ytd-playlist-panel-video-renderer:hover /*play list item */
  {
    background-color: rgba(245, 245, 245,0) !important;
    box-shadow: inset 0px 0px 10px 3px rgba(250, 250, 250,0
) !important;
    transition: none !important;
  }


/*hover off*/
  ytd-playlist-panel-renderer[collapsible] .header.ytd-playlist-panel-renderer
,.playlist-items.ytd-playlist-panel-renderer/*play list background*/
,ytd-playlist-panel-video-renderer /*play list item */
  {
    background-color: rgba(0,0,0,0) !important;
    box-shadow: none !important;
    transition: all 1s !important;
  }

/*primary text */
  #video-title.ytd-playlist-panel-video-renderer/*playlist video title*/
,ytd-playlist-panel-video-renderer[watch-color-update_] #index.ytd-playlist-panel-video-renderer, ytd-playlist-panel-renderer[collapsible] .index-message-wrapper.ytd-playlist-panel-renderer/*video number */
  {
    color: rgba(255, 255, 255,1) !important;
  }

/*secondary text */
  ytd-playlist-panel-video-renderer[watch-color-update_] #byline.ytd-playlist-panel-video-renderer /*playlist video uploader*/
  {
    color: rgba(250, 250, 250,1) !important;
  }
/*border*/

  ytd-playlist-panel-renderer[js-panel-height_]/*bottom outer playlist separator*/
  {
    border: 1px solid rgba(0, 0, 0,0) !important;
  }

  .header.ytd-playlist-panel-renderer/*top playist separator*/
  {
    border-bottom: 1px solid rgba(0, 0, 0,0) !important;
  }
    
/*  ---------------------------------------------------------------------------------- */


/*playist*/


  ytd-playlist-panel-renderer[js-panel-height_]/*bottom outer playlist separator*/
  {
    border: 1px solid rgba(0, 0, 0,0) !important;
  }

  .header.ytd-playlist-panel-renderer/*top playist separator*/
  {
    border-bottom: 1px solid rgba(0, 0, 0,0) !important;
  }
    
/*  ---------------------------------------------------------------------------------- */
/*icon*/
  
  yt-icon.ytd-badge-supported-renderer
,button.yt-icon-button > yt-icon:hover /* likes dislikes other icons*/
,.guide-icon.ytd-guide-entry-renderer:hover /* other icons */

,button.yt-icon-button > yt-icon:hover /* likes dislikes other icons*/
,.guide-icon.ytd-guide-entry-renderer:hover /* other icons */
  {
    color: rgba(255, 255, 255) !important;
    transition: all 0s !important;
  }

  button.yt-icon-button > yt-icon /* likes dislikes other icons*/
,.guide-icon.ytd-guide-entry-renderer /* other icons */
  {
    color: rgb(180, 180, 180) !important;
    transition: all 1s !important;
  }

  ytd-toggle-button-renderer.style-default-active[is-icon-button] /* active like text */
  {
    color: rgba(255, 255, 255,1) !important;
  }



/*  ---------------------------------------------------------------------------------- */
/* channel page */

  ytd-page-manager > *.ytd-page-manager /* video page background*/
  {
    background: transparent !important;
  }

  #channel-header.ytd-c4-tabbed-header-renderer
,#tabs-inner-container.ytd-c4-tabbed-header-renderer
  {
    background-image: linear-gradient(
  rgba(0, 0, 0,0.3),
  rgba(0, 0, 0,0.3)
         ),            
                                            
  url(https://i.postimg.cc/B6qFqCkp/1.jpg) !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
    background-size: cover !important;
  }

  paper-tab.ytd-c4-tabbed-header-renderer:hover/* home videos playist etc*/
,ytd-grid-video-renderer:hover /* video thumbnail */
,ytd-video-renderer:hover /* community thumbnail*/
  {
    background-color: rgba(245, 245, 245,0) !important;
    box-shadow: inset 0px 0px 10px 3px rgba(250, 250, 250,0
) !important;
    transition: none !important;
  }

/*hover off*/
  paper-tab.ytd-c4-tabbed-header-renderer /* home videos playist etc*/
,ytd-grid-video-renderer /* video thumbnail */
,ytd-video-renderer /* community thumbnail*/
  {
    background-color: rgba(0,0,0,0) !important;
    box-shadow: none !important;
    transition: all 1s !important;
  }

/*text */
/*primary */
    
  ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer /* video title */
,paper-tab.iron-selected.ytd-c4-tabbed-header-renderer /* selected tab text */
,#label-text.yt-dropdown-menu/* upload */
,#title.ytd-shelf-renderer/* home heading text */
,#subscriber-count.ytd-c4-tabbed-header-renderer/*subscriber*/
,#video-title.ytd-grid-playlist-renderer/*play list title*/
,#content-text.ytd-backstage-post-renderer/* community tumbnail text */
,#author-text.yt-simple-endpoint.ytd-backstage-post-renderer /*coummunity page chhannel names*/
,ytd-video-renderer[is-backstage-video] #video-title.ytd-video-renderer/* video name  community page*/
,#description-text.ytd-video-renderer/*video descriptions community page */
,paper-button.ytd-backstage-comments-renderer /* view more comments */
,#product-name.ytd-vertical-product-card-renderer/* shop tab product name*/
,#left-column.ytd-channel-about-metadata-renderer,#right-column.ytd-channel-about-metadata-renderer,tbody,.deemphasize.yt-formatted-string/* about page text */
,.title.ytd-mini-guide-entry-renderer/*home trend sub left side */
,#title.ytd-grid-channel-renderer,.title.ytd-mini-channel-renderer,#title.ytd-vertical-channel-section-renderer,ytd-subscribe-button-renderer[button-style=COMPACT_GRAY] paper-button.ytd-subscribe-button-renderer yt-formatted-string.ytd-subscribe-button-renderer/*other channels plus subcribed button*/
/*other  channel designs*/
,#video-title.ytd-video-renderer/*video titles home page */
  {
    color: rgba(255, 255, 255,1) !important;
  }
    

/*secondary text*/    
  paper-tab:not(.iron-selected) > .tab-content.paper-tab   /*video title */ 
,#metadata-line.ytd-grid-video-renderer span.ytd-grid-video-renderer /* video thumbnail views and date ago */
,#thumbnail-attribution.ytd-grid-channel-renderer/* suggested  channel subcribers*/
/*other  channel designs*/
,#subtitle.ytd-shelf-renderer/*category sub description */
  {
    color: rgba(250, 250, 250,1) !important;
  }   

/* icon*/

  .guide-icon.ytd-mini-guide-entry-renderer/* home trend sub left side */
,iron-icon.paper-icon-button/*home video tab right arrow */
  {
    color: rgb(180, 180, 180) !important;
    transition: all 1s !important;
  }

  .guide-icon.ytd-mini-guide-entry-renderer:hover/* home trend sub left side */
,iron-icon.paper-icon-button/*home video tab right arrow */
  {
    color: rgb(255, 255, 255) !important;
    transition: all 0s !important;
  }


/*  ---------------------------------------------------------------------------------- */
/* menusmenus  */
/* front page */




  ytd-compact-link-renderer:hover /*hover effects for menus top right*/
,ytd-menu-service-item-renderer[menu-refresh] paper-item.ytd-menu-service-item-renderer:hover/* thumbnail video option menu*/
  {
    background-color: rgba(245, 245, 245,0) !important;
    box-shadow: inset 0px 0px 10px 3px rgba(250, 250, 250,0
) !important;
    transition: none !important;
  }

  ytd-compact-link-renderer /*hover effects for menus top right */
,ytd-menu-service-item-renderer[menu-refresh] paper-item.ytd-menu-service-item-renderer/* thumbnail video option menu*/
  {
    background-color: none!important;
    box-shadow: none !important;
    transition: 3s !important;
  }

/*semi trans background*/
  ytd-active-account-header-renderer/*user channel name top right */
,ytd-simple-menu-header-renderer,#sections.ytd-multi-page-menu-renderer > ytd-background-promo-renderer.ytd-multi-page-menu-renderer/*notifications*/
,paper-listbox /* video thumbnail menu options*/
,.menu-container.ytd-multi-page-menu-renderer/*background for top right menus */
  {
    background-color: rgba(220, 220, 220,0.5) !important;
    box-shadow: none !important;
    transition: all 1s !important;
  }


/* need this as well for video thumbnail options */
  #contentWrapper.iron-dropdown > * /*video thumbnail drop donnw option background*/
  {
    background: transparent !important;
  }

/* text primary */
  #label.ytd-compact-link-renderer/*uplaod go live*/
,#account-name.ytd-active-account-header-renderer,#email.ytd-active-account-header-renderer /* user name account top right */
,h2.ytd-simple-menu-header-renderer/*notifications text*/
,ytd-menu-service-item-renderer[menu-refresh] yt-formatted-string.ytd-menu-service-item-renderer/* video menu options text */
  {
    color: rgba(255, 255, 255,1) !important;
  }
    





/* arrow  */
  .arrow.yt-horizontal-list-renderer /* right arrow*/
  {
    right: -35px;
    height: 180px;
    background: rgba(255, 255, 255, .3) !important;
    box-shadow: none !important;
  }

/*video page */
/*share save video*/
  paper-dialog /*background and outer border*/
  {
    background-color: rgba(0, 0,0,0.8) !important;
    border: 1px solid rgba(0, 0, 0,0) !important;
  }

  ytd-add-to-playlist-renderer[increased-tap-target] #playlists.ytd-add-to-playlist-renderer > *.ytd-add-to-playlist-renderer:not(:last-child):hover/*item hover */
,ytd-menu-navigation-item-renderer[menu-refresh] paper-item.ytd-menu-navigation-item-renderer:hover /*add translations special case!*/
  {
    background-color: rgba(245, 245, 245,0) !important;
    box-shadow: inset 0px 0px 10px 3px rgba(250, 250, 250,0
) !important;
    transition: none !important;
  }

  ytd-add-to-playlist-renderer[increased-tap-target] #playlists.ytd-add-to-playlist-renderer > *.ytd-add-to-playlist-renderer:not(:last-child) /*off hover */
  {
    transition: 3s !important;
  }

/*primary */
  #title.ytd-add-to-playlist-renderer,#label.checkbox-height.ytd-playlist-add-to-option-renderer /*all text in save menu */
,#title.yt-share-target-renderer/*share video social media texts */
,yt-formatted-string.yt-start-at-renderer/*start at*/
,#label.ytd-privacy-dropdown-item-renderer,  iron-input.paper-input > input.paper-input/* save to create new playlist */
,ytd-menu-navigation-item-renderer[menu-refresh] yt-formatted-string.ytd-menu-navigation-item-renderer/* add translations */
,yt-formatted-string.ytd-menu-service-item-renderer /*video thumbnail options */
  {
    color: rgba(255, 255, 255,1) !important;
  }

/*secondary text*/
  #description.ytd-privacy-dropdown-item-renderer /*  save to create new playlist */
  {
    color: rgba(250, 250, 250,1) !important;
  }

  #scroll-button-back.yt-third-party-share-target-section-renderer
,#scroll-button-forward.yt-third-party-share-target-section-renderer
  {
    right: -35px;
    height: 50px;
    background: rgba(250, 250, 250, .3) !important;
    box-shadow: none !important;
  }




/* channel page */

  paper-item:hover/*sort by */
  {
    background-color: rgba(245, 245, 245,0) !important;
    box-shadow: inset 0px 0px 10px 3px rgba(250, 250, 250,0
) !important;
    transition: none !important;
  }

  paper-item /*sort by */
  {
    box-shadow: none !important;
    transition: all 1s !important;
  }


/*primary text */
  .item.yt-dropdown-menu /*sort by text*/
  {
    color: rgba(255, 255, 255,1) !important;
  }

/*---------------------------------------------------------------------------------- */


/*borders*/
/*front page */

  h3.ytd-guide-section-renderer/* left side category bottom */
,#masthead-container.ytd-app/* top bar search etc bottom border */
,#rich-shelf-header.ytd-rich-shelf-renderer,#show-more-button.ytd-rich-shelf-renderer,#grid-header.ytd-rich-grid-renderer
/*bottom borders of front page */
  {
    border-bottom: 1px solid rgba(0, 0, 0,0) !important;
  }

  ytd-mini-guide-renderer.ytd-app /*left side separator*/
,#guide-inner-content.ytd-app /*left side expanded category*/
  {
    border-right: 1px solid rgba(0, 0, 0,0) !important;
  }
/*  ---------------------------------------------------------------------------------- */



/*  ---------------------------------------------------------------------------------- */
/* video page */

  #top-row.ytd-video-secondary-info-renderer
  {
    border-top: 1px solid rgba(0, 0, 0,0) !important;
  }

  #title.ytd-comments-header-renderer /* bottom video border */
  {
    border-top: 1px solid rgba(0, 0, 0,0) !important;
  }

  ytd-watch-flexy[flexy_] #primary.ytd-watch-flexy
  {
    border-right: 1px solid rgba(0, 0, 0,0) !important;
    position: relative !important;
    left: -5px !important;
  }



/*channel page */

  #tabsContainer.paper-tabs/*channel tabs */
  {
    border-bottom: 1px solid rgba(0, 0, 0,0) !important;
  }

  #selectionBar.paper-tabs /*selected tab and transition timing */
  {
    border-bottom: 1px solid rgba(0, 0, 0,0) !important;
    transition: all 0.5s !important;
  }

/*like dislike bar */
  #container.ytd-sentiment-bar-renderer/*dislike bar */
  {
    background-color: rgb(255, 255, 255)  !important;
  }

  ytd-sentiment-bar-renderer[activated_] #like-bar.ytd-sentiment-bar-renderer
,#like-bar.ytd-sentiment-bar-renderer/*like bar*/
  {
    background-color: rgb(180, 180, 180)!important;
  }


/*  ---------------------------------------------------------------------------------- */






/*  ---------------------------------------------------------------------------------- */
/*search box */

  .sbdd_b /*need both for borders */
  {
    border: 1px solid rgba(0, 0, 0,0) !important;
  }

  .sbsb_a,#sbse1,.sbdd_b /*background transparent */
,.sbfl_b,ul,sbsb_c gsfs,.sbdd_b /*report search predictions*/
,.sbsb_c /*hover off timer */
  {
    background-color: rgba(0, 0, 0,0.2) !important;
    transition: all 1s !important;
  }

  .sbsb_c:hover /*hover item*/
  {
    background-color: rgba(245, 245, 245,0)  !important;
    transition: all 0s !important;
  }

  #container.ytd-searchbox input.ytd-searchbox, /*search box */
.sbsb_c /*drop downtext */
  {
    color: white !important;
  }

  .sbpqs_a/*visited */
  {
    color: rgba(250, 250, 250,1) !important;
  }





/*  ---------------------------------------------------------------------------------- */

/*search box*/


  #container.ytd-searchbox,#search-icon-legacy.ytd-searchbox /* background*/
  {
    background: transparent !important;
    border: 1px solid rgba(0, 0, 0,0)  !important;
    box-shadow: None !important;
    transition: all 1s !important;
  }
/* primary text*/
  #container.ytd-searchbox input.ytd-searchbox /* search box
{
color:rgba(255, 255, 255,1) !important;
}


#container.ytd-searchbox:hover, #search-icon-legacy.ytd-searchbox:hover

{
background-color:rgba(245, 245, 245,0) !important; 
box-shadow:  0px 0px 10px 3px rgba(219, 24, 76,0.5) !important;
transition: all 0s !important

}
/*--------------------------------------------------------------------------------*/
/*search results*/
#description.ytd-channel-renderer/*channnel description  */
,#text.ytd-channel-name/*channnel name*/
,h3.ytd-playlist-renderer/*play list title*/
  {
    color: rgba(255, 255, 255,1) !important;
  }
    

/*primary    */
  #video-title.ytd-child-video-renderer, #length.ytd-child-video-renderer/*video title and time*/
,#video-title.ytd-movie-renderer/*filmtitle */
,#description-text.ytd-movie-renderer/*film description*/
  {
    color: rgba(255, 255, 255,1) !important;
  }   


/*secondary  text  */
  #metadata.ytd-channel-renderer /*channnel results subscribers etc*/
,.movie-metadata-list.ytd-movie-renderer li.ytd-movie-renderer /*film actors director sub text etc*/
