    color: rgba(250, 250, 250,1) !important;
  }   


/* search page borders */

  #dismissable.ytd-video-renderer /*video result separator */
,#content-section.ytd-channel-renderer /*channel results */
,ytd-playlist-renderer /*playlist results*/
  {
    border-bottom: 1px solid rgba(0, 0, 0,0) !important;
    padding-bottom: 5px  !important;
  }


/*hover on*/

  #content-section.ytd-channel-renderer:hover /*channel search results */
,ytd-playlist-renderer:hover/*play list results*/
  {
    background-color: rgba(245, 245, 245,0) !important;
    box-shadow: inset 0px 0px 10px 3px rgba(250, 250, 250,0
) !important;
    transition: none !important;
  }

/*hover off*/
  #content-section.ytd-channel-renderer /*channel search results */
,ytd-playlist-renderer /*play list results*/
  {
    background-color: rgba(0,0,0,0) !important;
    box-shadow: none !important;
    transition: all 1s !important;
  }
}
