css:
@-moz-document url("https://catanuniverse.com/en/game/") {
header, footer {
  display: none;
}

body {
  overflow-y: hidden;
}

body #page {
  padding-top: 0;
}

.vc_column_container>.vc_column-inner {
  padding: 0;
}

.game-content-control {
  position: absolute;
  z-index: 2;
}

.template-wrap {
  width: inherit;
  margin: 0;
}

.game-view {
  width: 100%;
}

}
