css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://lexin.nada.kth.se/cgi-bin/swe-eng") {


HTML > BODY { background-color:#fcfcfc !important; }
/* Hiding Header */ 
HTML > BODY > TABLE:first-child { display:none !important; }
/* Hiding Explain and look at this footer */ 
TABLE[cellspacing="0"][cellpadding="5"][border="1"] { display:none !important; }
/* Hiding Footer */ 
.fotepost {display:none !important;}  

HTML > BODY > TABLE:last-child {  
  width:100% !important; 
  margin:120px auto 0 !important; 
}
HTML > BODY > TABLE:last-child > TBODY > TR > TD > FORM > TABLE, 
HTML > BODY > TABLE:last-child > TBODY > TR > TD > FORM > TABLE TABLE {  width:100% !important;}

td.menu {
  width:0px !important;
  font-size:1px !important;
  background-color:transparent !important;
}

td.menu > img, td.menu > a, td.menu > br {display:none !important;}
td.menu > table:not(:first-child) { 
  height:0 !important; 
  width:0 !important;
  margin:0 !important; 
  padding:0 !important;
  background-color:transparent !important;
}
td.menu > table:not(:first-child) span.menu { display:none !important; }
td.menu > table:not(:first-child) img { display:none !important; }
td.menu > table:not(:first-child) br { display:none !important; }
td.menu > table:not(:first-child) td { 
  height:0 !important; 
  width:0 !important;
  margin:0 !important; 
  padding:0 !important;
  background-color:transparent !important;  
}

td.menu table {
  width:0px !important;
  font-size:1px !important;
}

td.menu table:first-child {
  visibility:visible  !important;
  width:100% !important;
  position:absolute !important;
  top:45px !important;
  right:0 !important;  
  font-size:12px !important;
}

td.menu table:first-child br {  display:none  !important;}
td.menu table:first-child b span.menu {  display:none  !important;}
td.menu table:first-child td {  background-color:transparent !important; padding: 0 10% 0 10% !important;}

td.menu table:first-child label span.menu, td.menu table:first-child span.menu {
  font:normal 11px/10px "lucida grande" !important;
  display:inline-block !important;
}

#kallsprak,#malsprak, input[type="checkbox"][name="stava"] {
  margin:0 !important;
  display:inline-block !important;  
}

INPUT[type="image"][name="forra"] { 
  position: absolute !important;
  top:140px; right:10%;
  margin-right:30px !important;
  visibility:visible !important;
  opacity: 0.5 !important;
}
INPUT[type="image"][name="nasta"] {
  position: absolute !important;
  top:140px; right:10%; 
  visibility:visible !important;
  opacity: 0.5 !important;
}

INPUT[type="image"]:hover {
  opacity:1 !important;
}

INPUT[name="uppslagsord"] {
  position:absolute !important;
  left:10% !important;
  right:10% !important;
  top:70px !important;
  width:80% !important;
  height:48px !important;
  padding:0px 8px !important;
  font:bold 24px/44px "lucida grande", "trebuchet ms" !important;
  background-color:#fff !important;
  border:2px solid #000 !important;
  z-index:4 !important;
  overflow: hidden !important;
  display:block !important;
 }

INPUT[name="soktyp"] {
  display:none !important;
  position:absolute !important;
  right:10% !important;  
  top:20px !important;
  width:36px !important;
  height:36px !important;
  z-index:5 !important;
  border:1px solid red !important;
  background-color:green !important; 
  text-indent:-500px !important;  
  background-image:url('data:image/gif;base64,;') !important;
}


TD[height="47"][bgcolor="#efde9c"] {   background-color: transparent !important;  }
TD[height="47"][bgcolor="#efde9c"] br { display:none !important; }
TD[height="47"][bgcolor="#efde9c"] h4 { padding:0 10% !important;   font:bold 11pt/13pt "candara", "droid sans", "trebuchet ms"; margin:9px 60px 0 -16px !important; }
TD[height="47"][bgcolor="#efde9c"] h3 { padding:0 10% !important;   font:bold 11pt/13pt "candara", "droid sans", "trebuchet ms"; margin:9px 60px 0 -16px !important;}

HTML > BODY > TABLE:last-child > TBODY > TR > TD > FORM > TABLE > TBODY > TR > TD > TABLE > TBODY > TR:first-child > TD:last-child > TABLE > TBODY > TR:last-child > TD > TABLE > TBODY > TR > TD:last-child {
  padding:0 12% !important;
}

HTML > BODY > TABLE:last-child > TBODY > TR > TD > FORM > TABLE > TBODY > TR > TD > TABLE > TBODY > TR:first-child > TD:last-child > TABLE > TBODY > TR:last-child > TD > TABLE > TBODY > TR > TD:first-child {
  display:none !important;
}

dl dt {
  font:bold 10pt/13pt "candara", "droid sans", "trebuchet ms";
  color:#343434 !important;
}

dd {font:normal 10pt/13pt "segoe ui";}
dd b { font:bold 18pt/19pt "droid serif"; }
dd li { font:normal 11pt/13pt "segoe ui" !important;  list-style-type: decimal;}
dd li i { padding-right: 5px !important;}

hr {
  border:0 !important;
  border-top:1px solid #000 !important;
  margin:15px auto !important;
  height:0px !important;
  opacity:0.3 !important;
}


/** {border:1px solid red !important}*/

}
