@-moz-document domain("www.golgo31.net") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
body > center
, body > center div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
body > center * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
body > center a:link { color : #0000ee !important; }
body > center a:visited { color : #551a8b !important; }
body > center a:hover { color : #ee0000 !important; }
body > center a:hover { background-color : transparent !important; }
/* /destroy div layout */

td[width="227"][bgcolor="#ccffcc"]
, table[cellspacing="1"][cellpadding="1"] > tbody > tr:first-child
, table[cellspacing="1"][cellpadding="1"] > tbody > tr:first-child + tr
, a[href="http://readmej.com/"]
, img[src="golgo31.jpg"]
, body > center > table:first-child
, body > center > table > tbody > tr > td[width="300"]
, td[width="487"] > table[width="482"] > tbody > tr:first-child > td:first-child
{ display : none !important; }

}
