css:
@-moz-document domain("vkontakte.ru") {
	* {
		background-color: #000000 !important;
		font-family : terminus;
		color: #00ff00 !important;
		border-color: #000000 !important;
	}
	
	body {
		background-color: #000000!important;
	}
	
	#pageHeader {
		background-color: #000000 !important;
		background-image: none !important;
		border: solid 0px #000000 !important;
	}
	#pageHeader * {
		background-color: #000000!important;
	}

	#topNav li {
		background: none !important;
	}
	/*#quickSearch,#quickSearch * {
		background-color: #000000 !important;
	}
	#quickSearch #qinput {
		border:1px solid #00ff00 !important;
	}*/

	div#pageBody, div.pageBody {
		margin-top:5px !important;
	}
	div#sideBar {
		margin-top:5px !important;
	}
	#sideBar {
		background: none !important;
	}

	#nav li {
		border-bottom: solid 1px #000000 !important;
	}
	#nav>li>a {
		background-color: #000000 !important;
		color: #66ffff!important;
		border-width: 0 0 1px 0 !important;
		border-top: 0px !important;
		border-bottom: solid 0px!important;
	}
	#nav>li>a:hover {
		background-color: #66ffff !important;
		color: #000000 !important;
		border-bottom: solid 0px  !important;
	}

	.moreDiv {
		border-top: solid 1px #ffff66 !important;
	}

	#banner1,#banner2 {
		display: none !important;
	}

	a {
		color: #ffff66 !important;
		background: none !important;
	}

	#userProfile {
	background: none black !important;
	}

	#rateLeft{
		background-color: #000000 !important;
	}

	.flexHeader, .feedDay {
		background-color: #000000 !important;
		border-bottom: solid 1px #33ffff !important;
	}

	.pageList a {
		border-bottom:0px solid #ffffff !important;
	}
	.pageList a:hover {
		border-bottom:0px solid red !important;
	}

	.tBar {
		border-bottom: solid 0px red !important;
	}
	ul.tabs li {
		background: none #000000 !important;
		border: 0 !important;
	}
	.tabs > li > a {
		background: red none;
	}
	.tabs > li > a > b {
		visibility: hidden !important;
	}
	.tabs > li.activeLink > a {
		background-color: #333300!important;
		border-bottom: solid 0px !important;
		margin-bottom: 0px !important;
	}
	.tabs > li:hover > a {
		background-color: #333333 !important;
		border-bottom: solid 0px !important;
	}

	#wrap1,#wrap2,#wrapH,#wrapHI {
		border-left:0px solid !important;
		border-right:0px solid !important;
		border-color: red !important;
	}
	.newRow {
		background-color: #330000 !important;
	}
	#header {
		background-color: black !important;
		background-image: none !important;
		border-left: 0 !important;
		border-right: 0 !important;
		border-top: 0px !important;
		border-bottom: 0px !important;
	}
	#header h1 {
		background-color: black !important;
	}
	#header a {
		color:#ffffff !important;
	}

	#content,#friendsCont {
		border: 0 !important;
	}
	.result,.clearFix {
		border: 0 !important;
	}

	#checkboxFeed {
		border-bottom:1px solid red !important;
	}

	.feedPhotos a img {
		border: 1px solid #00ff00 !important;
	}
	.feedPhotos a:hover img {
		border: 1px solid #ffffff !important;
	}

	.mailbox table td {
		border-bottom:1px solid #00ff00 !important;
	}

	.postHeader {
		border-top: 0 !important;
	}

	#notesBar, #mainPanel {
		background: black none !important;
	}

	.privacy_panel_editor {
		border: 0 !important;
	}
	.privacy_panel_border {
		border:10px solid red!important;
	}

	input[type="text"],input[type="password"] {
		border:1px solid #00ff00 !important;
	}

	#startQuote, #endQuote {
		background: red none !important;
	}

	.buttonRow {
		border: 0 !important;
	}
	.buttonRow a {
		background: none #000000 !important;
		border: 0px solid #606060 !important;
		-moz-border-radius-topleft: 2px;
		-moz-border-radius-topright: 2px;
		-moz-border-radius-bottomleft: 2px;
		-moz-border-radius-bottomright: 2px;
	}
	.buttonRow b {
		background: none transparent !important;
		margin: 0 !important;
		padding: 0 !important;
		border: 0 !important;
	}

	ul.nNav {
		border: 0 !important;
		margin: 0 !important;
	}
	ul.nNav .nc {
		display: none !important;
		border: 0 !important;
		margin: 0 !important;
	}
	ul.nNav a {
		background: none black !important;
		border: 1px solid #ffff00 !important;
		padding: 4px 10px !important;
		margin: 0 !important;
	}

	#audioBar, .profile, #group.profile, .notesEdit {
		background: transparent none !important;
	}

	input[type="text"], input[type="password"], textarea {
		color: #00ff00 !important;
		border: 1px solid #ffff00 !important;
		background-color: #000000 !important;
		border: 1px solid #0000ff;
	}
}
