css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rockpapershotgun.com") { 
  #ecd_opt_in_banner, #sidebar, #footer, #recommendations-wrapper, .top-features, .tags, .featured-block-title { display: none !important; }
  #content, .content { width:738px !important; }
  #page { width: 1038px; padding-left:300px; }
  .leaderboards { display:inline-block; margin:10px; }
  
  #header .logo a { background-position:50% 50%;
    height:76px !important; 
    display:block !important; position:static !important; margin:auto !important; }
  #header-content > [id] { display:none; }
  
  p, li { font-family:Calibri,Arial,sans-serif !important; line-height:1.4em !important; }
}
