/* ==UserStyle==
@name           Bestest Subrocks V1.0.1
@namespace      userstyles.world
@version        1.0.1
==/UserStyle== */
@-moz-document domain(subrock.rocks) {
img:not(.www-header-mast) {
    height: 100px;
}


.www-header-mast img {
    width: 1000px;
}

.www-core-container {
    background-color: pink;
    width: unset;
    margin: unset;
}

body {
    font: unset;
    font-size: 8px;
    width: unset;
    margin: unset;
    overflow-x: unset;
}

html {
    background-color:#ff002d;
}

.www-motto {
    color: red;
    vertical-align: top;
    position: relative;
    left: 3px;
    top: 1px;
    font-size: 100px;
}

.alert {
    background-color: #00ff58;
    text-align: unset;
    font-weight: unset;
    height: unset;
    padding-top: unset;
    border-radius: unset;
    margin-bottom: 100px;
}
}
