css:
@-moz-document url-prefix("https://community.chineseswordacademy.com") {
#panel {
    color: #fff;
    background-color: #000;
}

#mainHeader {
    color: #fff;
    background-color: #000;
}

/* Left side bar header */
#panel > div > div.sidebar.sidebar--left > div > div > div > div.block-header.block-header--inactive > a {
    color: #fff;
}

/* Left side bar sub groups */
#panel > div > div.sidebar.sidebar--left > div > div > div > div.block-content > a > span.space__name {
    color: #fff;
}

/* Left side bar links */
#panel > div > div.sidebar.sidebar--left > div > div > div.sidebar__block.block--links > div.block-header > a {
    color: #fff;  
}

/* Left side bar sub links */
#panel > div > div.sidebar.sidebar--left > div > div > div.sidebar__block.block--links > div.block-content > a {
    color: #fff;
}

/* Trending posts */
#panel > div > div.sidebar.sidebar--right > div > div:nth-child(2) > div > div.block.sidebar__block.sidebar__right.trending-posts {
    color: #fff;
    background-color: #000;
}

/* Trending user */
#panel > div > div.sidebar.sidebar--right > div > div > div > div.block.sidebar__block.sidebar__right.trending-posts > div.block__content > ul > li > div > div > a.trending-posts__author-name {
    color: #fff;    
}

/* get started */
#panel > div > div.sidebar.sidebar--right > div > div:nth-child(2) > div > div.block.sidebar__block.sidebar__right.sidebar__member-onboarding {
    color: #fff;
    background-color: #000;   
}

/* members right panel header */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.sidebar.sidebar--right > div > div > div > div.block__title > h2 > a {
    color: #fff;
    background-color: #000;    
}

/* members right panel members name */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.sidebar.sidebar--right > div > div > div > div.block__inside > ul > li > span > a {
    color: #fff;
    background-color: #000;     
}

/* members right panel see all link */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.sidebar.sidebar--right > div > div > div > div.block__inside > a {
    color: #fff;
    background-color: #000;  
}

/* get welcome header background */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div.post.post--parent.is-truncation-disabled.is-pinned.post-meta--hidden > div > div.post__header.post-meta--hidden {
    background-color: #000;  
}

/* get welcome header text */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div.post.post--parent.is-truncation-disabled.is-pinned.post-meta--hidden > div > div.post__header.post-meta--hidden > h2 > a {
    color: #fff; 
}

/* welcome general background */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div.post.post--parent.is-truncation-disabled.is-pinned.post-meta--hidden > div > div.post__content {
    background-color: #000;  
}

/* welcome text */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div.post.post--parent.is-truncation-disabled.is-pinned.post-meta--hidden > div > div.post__content > div > div > div > div {
    color: #fff;  
}

/* welcome links */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div.post.post--parent.is-truncation-disabled.is-pinned.post-meta--hidden > div > div.post__content > div > div > div > h2 {
    color: #fbfbfb; 
}

/* welcome list */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div.post.post--parent.is-truncation-disabled.is-pinned.post-meta--hidden > div > div.post__content > div > div > div > ul > li {
    color: #fbfbfb; 
}

/* say hello posts list */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div.space__posts.space__posts--list > div {
    background-color: #000;
}

/* say hello posts list username */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div.space__posts.space__posts--list > div > div > div > div.post--list__body > div.post--list__content > div.post--list__header > h2 > a {
    color: #fff;
}

/* Main posts */
#panel > div > div.main > div > div > div.space__posts.space__posts--posts > div > div > div {
    background-color: #000;
}

/* main posts tools like and comment */
#panel > div > div.main > div > div > div.space__posts.space__posts--posts > div > div > div > div > div.post__actions.post__actions-visible {
    color: #fff;
    background-color: #000;
}

/* main posts header */
#panel > div > div.main > div > div > div.space__posts.space__posts--posts > div > div > div > div.post__content > div.post__header > h2 > a {
    color: #fbfbfb;
}

/* main posts text */
#panel > div > div.main > div > div > div.space__posts.space__posts--posts > div > div > div > div.post__content > div.post__body > div > div {
    color: #fff;
}

/* main posts username */
#panel > div > div.main > div > div > div.space__posts.space__posts--posts > div > div > div > div.post__content > div.post__user > div.post__bio > div.author.has-tags > div.author__name > a {
    color: #fff; 
}

/* main posts where it was posted */
#panel > div > div.main > div > div > div.space__posts.space__posts--posts > div > div > div > div.post__content > div.post__user > div.post__bio > div.post__meta > span {
    color: #fff;
}

/* main posts see more link */
#panel > div > div.main > div > div > div.space__posts.space__posts--posts > div > div > div > div.post__content > div.post__body > label {
    color: #fff;
}

/* other posts */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div > div > div > div > div > div > div > div.post__post {
    background-color: #000;
}

/* other posts tools like and comment */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div > div > div > div > div > div > div > div > div.post__actions.post__actions-visible {
    color: #fff;
    background-color: #000;
}

/* other posts header */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div > div > div > div > div > div > div > div.post__post > div.post__header > h2 > a {
    color: #fbfbfb;
}

/* other posts engagement */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div > div > div > div > div > div > div > div > div.post__engagement {
    color: #fff;
    background-color: #000;  
}

/* other posts text */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__content > div > div > div > div > div > div > div > div.post__post > div.post__content > div.post__body > div > div {
    color: #fff;
}

/* other posts header text */
#panel > div > div.main > div > div.main__content > div > div > div > div > div > div > div.post__post > div.post__header > h2 > a {
    color: #fff;
}

/* other posts tools like and comment open */
#panel > div > div.main > div > div.main__content > div > div > div > div > div > div > div > div.post__actions.post__actions-visible {
    color: #fff;
    background-color: #000;  
}

/* other posts 2 background */
.trix-v2 .react-trix-editor-container trix-editor h1, .trix-v2 .react-trix-editor-container trix-editor h2, .trix-v2 .react-trix-editor-container trix-editor h3, .trix-v2 .trix-content h1, .trix-v2 .trix-content h2, .trix-v2 .trix-content h3 {
    color: #fff;
}
    
/* reply comments posts header announcements */
#panel > div.standard-layout.has-sidebar.trix-v2 > div.main > div > div.main__header.main__header--sticky {
    color: #fff;
    background-color: #000;  
}

/* reply comments expanded background */
div > div > div > div > div.post__body {
    color: #fff;
    background-color: #000;  
}


/* reply comments expanded header and about */
div > div > div > div > div.post__user > div.post__bio {
    color: #fff;
    background-color: #000; 
}

/* reply comments expanded username */
div > div > div > div > div.post__user > div.post__bio > div.author > div.author__name > a {
    color: #fff;
}

/* reply comments expanded general background */
div > div > div > div {
    background-color: #000; 
}

/* reply comments expanded general text */
.trix-v2 .react-trix-editor-container trix-editor, .trix-v2 .trix-content {
    color: #fff;
}

/* reply comments expanded response background */
#all_comments > div.post__reply.reply-to-post {
    background-color: #000; 
}

/* reply comments expanded response background 1 */
#all_comments > div.post__reply.reply-to-post > form > div > div > div.comment-box__bar.expanded > div {
    background-color: #000; 
}

/* reply comments expanded response background 2 */
#all_comments > div.post__reply.reply-to-post > form > div > div > div.trix-editor-textbox.form-control.expanded {
    background-color: #000; 
}

/* reply comments expanded response background 3 */
#all_comments > div.post__reply.reply-to-post > form > div:nth-child(1) > div > div.trix-editor-textbox.form-control {
    background-color: #000; 
}

/* reply comments expanded response background 4 */
#all_comments > div.post__reply.reply-to-post > form > div:nth-child(1) > div > div.comment-box__bar > div {
    background-color: #000; 
}
    
/* reply comments expanded response background 5 */
div > div.post__replies > div > form > div > div > div.trix-editor-textbox.form-control.expanded > trix-editor > div {
    background-color: #000; 
}
    
/* reply comments expanded response background 6 */
.trix-v2 .react-trix-editor-container .toolbar {
    background-color: #000; 
}
    
/* reply comments expanded response background 7*/
.view-post .post__reply.reply-to-parent {
    background-color: #000; 
}

/* reply comments expanded response typing area */
#all_comments > div.post__reply.reply-to-post > form > div > div > div.trix-editor-textbox.form-control > trix-editor {
    color: #fff;
    background-color: #000; 
}
    
/* edit comment body */
#panel > div > div.form-wrapper > div > form > div.trix-content.enter-only > div > div > div > div.trix-editor-textbox.form-control > trix-editor > div {
    background-color: #000; 
}
    
/* edit comment header */
#panel > div > div.form-wrapper > div > h1 {
    background-color: #000; 
}
    
/* edit comment general 1 */
.standard-layout.has-sidebar .form-wrapper .form-content {
    background-color: #000;     
}
    
/* edit comment general 2 */
#panel > div > div.form-wrapper > div > form > div.trix-content.enter-only > div > div > div > div.trix-editor-textbox.form-control {
    background-color: #000;     
}

/* creating a post words create post */
#headlessui-dialog-2 > div > div > div > form > div.modal__box.quickpost-modal.false.modal__fullscreen > div > div.modal__header > div > div.modal__header--center > span {
    color: #fff;
}

/* creating a post text box */
#headlessui-dialog-2 > div > div > div > form > div.modal__box.quickpost-modal.false.modal__fullscreen > div > div.modal__body > div > div > div > div > div > div.trix-content.trix-content--post > div > div > div.trix-editor-textbox.form-control > trix-editor {
    color: #fff;
    background-color: #000; 
}

/* creating a post misc background 1 */
#headlessui-dialog-2 > div > div > div > form > div.modal__box.quickpost-modal.false.modal__fullscreen > div > div.modal__body {
    background-color: #000;     
}

/* creating a post misc background 2 */
#headlessui-dialog-2 > div > div > div > form > div.modal__box.quickpost-modal.false.modal__fullscreen > div > div.modal__header {
    background-color: #000;  
}

/* creating a post misc background 3 */
#headlessui-dialog-2 > div > div > div > form > div.modal__box.quickpost-modal.false.modal__fullscreen > div > div.quickpost-modal__footer > div {
    background-color: #000; 
}

/* creating a post misc background 4 */
#headlessui-dialog-2 > div > div > div > form > div.modal__box.quickpost-modal.false.modal__fullscreen > div > div.quickpost-modal__footer {
    background-color: #000; 
}

/* creating a post title */
#post_name {
    color: #fff;
    background-color: #000; 
}

/* members background */
#panel > div > div.main > div > div.main__content > div > div.space__members-listing > div > div > a {
    background-color: #000;  
}

/* members username */
#panel > div > div.main > div > div.main__content > div > div.space__members-listing > div > div > a > div.user__details > h4 {
    color: #4465ca;  
}

/* members tagline */
#panel > div > div.main > div > div.main__content > div > div.space__members-listing > div > div > a > div.user__details > div.user__tagline {
    color: #b5b5b5;  
}

/* members location */
#panel > div > div.main > div > div.main__content > div > div.space__members-listing > div > div > a > div.user__details > div.user__location {
    color: #b5b5b5;  
}

/* members joined */
#panel > div > div.main > div > div.main__content > div > div.space__members-listing > div > div > a > div.user__details > div.user__joined {
    color: #b5b5b5;  
}

/* members page spaces */
#panel > div > div.main > div > div.main__content > div > div > div {
    background-color: #000;  
}
    
/* members page card */
.member-card__inside {
    background-color: #000; 
}

/* members page spaces titles */
#panel > div > div.main > div > div.main__content > div > div > div > div > div.space-card__name > div.space-card__title > a > span {
    color: #b5b5b5;  
}

/* members page tagline */
#panel > div > div.main > div > div.main__header > div.member__profile > div.member__details > div.member__header > div.member__name-tagline > div.member__tagline {
    color: #b5b5b5;  
}

/* members page logcation */
#panel > div > div.main > div > div.main__header > div.member__profile > div.member__details > div.member__footer {
    color: #b5b5b5;  
}

/* members page bio */
#panel > div > div.main > div > div.main__header > div.member__profile > div.member__details > div.member__bio {
    color: #b5b5b5;  
}

/* members page recent posts header */
#panel > div > div.main > div > div.main__content > div > div > div.post__post > div.post__header > h2 > a {
    color: #b5b5b5;  
}

/* members page recent posts reactions */
#panel > div > div.main > div > div.main__content > div > div > div > div.post__actions.post__actions-visible {
    background-color: #000;  
}

/* private chat background 1 */
.chat-room__link {
    color: #fff;
    background-color: #000; 
}
    
/* private chat chatters left */
.chat-room__name span {
    color: #fff;
    background-color: #000; 
}
    
/* private chat chatters top */
.chat-room__header-name a {
    color: #fff;
    background-color: #000; 
}
    
/* private chat reply box */
.chat-room__text-editor .message-text-box {
    color: #fff;
    background-color: #000; 
}
    
/* private chat message box */
.chat-room__messages {
    color: #fff;
    background-color: #000; 
}

/* private chat message background */
.message__text {
    background-color: #363636;
}

/* private chat reply background */
.message--sent .message__text {
    background-color: #364577; 
}

