css:
@-moz-document domain("dota2.ru") {
#quick-reply{
    max-height: 85%;
    
    text-align: center;
    padding: 20px;
    background-color: #1a1b20;
    box-shadow: 0 0 6px 2px black;
    position: fixed;
    left: 0;
    width: 100% !important;
    z-index: 1000;
    transform: translateY(100%);
    bottom: 60px;
    transition: transform 0.3s, bottom 0.3s;
}

@media (max-width: 1200px) {
    #quick-reply {
        width: 100% !important;
    }
}

#quick-reply:before{
    content: 'Написать ответ';
    width: 100%;
    text-align: center;
}

#quick-reply:hover{
    overflow-y: auto;
    bottom: 0px;
    transform: translateY(0%);
}

#quick-reply > form{
    width: calc(100% - 178px);
    margin: 0 auto;
}

#send-message{
    width: 100%;
    margin: 0;
}

@media (max-width: 900px){
   #quick-reply > form{
    width: 100%;
    margin: 0 auto;
} 
}
}
