css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #006994;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }


  .card--grit
  {
    background-image: url("https://previews.123rf.com/images/photohampster/photohampster1904/photohampster190400001/123899321-beach-blurred-vertical-background-tropical-island-paradise-sandy-shore-washing-by-the-wave-blue-ocea.jpg");
  }


  html
  {
    background: fixed url("https://images.unsplash.com/photo-1507525428034-b723cf961d3e?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8c3VtbWVyJTIwYmFja2dyb3VuZHxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #006994;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 500%;
  }


  .bg--gradient
  {
    background: top left repeat, linear-gradient(90deg, #006994 60%, #006994 500%);
  }


  .card--a
  {
    background-image: url("https://media.istockphoto.com/photos/tropical-beach-copy-space-scene-picture-id1144456717?k=6&m=1144456717&s=612x612&w=0&h=6LzYM7XVZ1oN9yf_FCvsEnJbgHeTSr1sI4xGEbw6RVM=");
    color: #006994;
    background-size: 500%;
  }


  .btn--primary
  {
    background: #006994;
    color: #006994;
  }


  .theme--pDefault .profile-title
  {
    color: #006994;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-levelNum
  {
    color: #006994;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-body
  {
    color: #006994;
  }
}


@-moz-document domain(nitrotype.com)
{


  .dash-copyContainer
  {
    background: #006994;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }


  .dash-copy
  {
    color: #ffffff ;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }


  ::selection
  {
    background-color: #006994;
    color: white;
  }


  .bg--gradient
  {
    background: url(https://media.istockphoto.com/photos/empty-wooden-planks-with-blur-beach-and-sea-on-background-picture-id1234887973?b=1&k=6&m=1234887973&s=170667a&w=0&h=2p3msZif7yKoWuTuE6bZhyKzMhuFAaiVk8_8tqz8VDk=) no-repeat;
  }


  .theme--pDefault .profile-bg
  {
    background: url("https://media.istockphoto.com/photos/empty-wooden-planks-with-blur-beach-and-sea-on-background-picture-id1234887973?b=1&k=6&m=1234887973&s=170667a&w=0&h=2p3msZif7yKoWuTuE6bZhyKzMhuFAaiVk8_8tqz8VDk=") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #006994;
  }


  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #006994;
  }


  .theme--pDefault .profile-bgHead
  {
    background: url("https://media.istockphoto.com/photos/empty-wooden-planks-with-blur-beach-and-sea-on-background-picture-id1234887973?b=1&k=6&m=1234887973&s=170667a&w=0&h=2p3msZif7yKoWuTuE6bZhyKzMhuFAaiVk8_8tqz8VDk=") top left repeat, linear-gradient(0deg, #00699410, #006994 100%);
  }


  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://media.istockphoto.com/photos/empty-wooden-planks-with-blur-beach-and-sea-on-background-picture-id1234887973?b=1&k=6&m=1234887973&s=170667a&w=0&h=2p3msZif7yKoWuTuE6bZhyKzMhuFAaiVk8_8tqz8VDk=") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }


  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #006994;
  }
}
