css:
@-moz-document url-prefix("https://10ff.net") {
html{
	line-height: .5;
        -webkit-text-size-adjust: 100%;
}


:root {
	--secondarycolor: rgb(255, 34, 5);
	--primarycolor: rgb(134, 255, 5);
	--bgcolor: #161616; 
	--typingtextcolor: rgb(212, 242, 181); 
	cursor: url(https://cur.cursors-4u.net/others/oth-6/oth551.cur), auto !important;
	
}

body {
	background: var(--bgcolor);
	font-size: 20px;
	
	padding: 0;
	color: var(--primarycolor);
}

::-webkit-scrollbar {
  display: none;
}

.overlayer{
	font-size: 6em;
	text-align: center;
	color: var(--secondarycolor);
	margin-top: 2em;
	background: transparent;
	
}

.overlayer-title{
	font-size: 5em;
	height: 100%;
	text-align: center;
	background: rgba(23,23,23,.5);
	color: var(--secondarycolor);
	padding: 5px;
}

.server-information {
	background-color: var(--bgcolor);
	color: var(--primarycolor);
	margin-top: 10px;
	margin-left: 30%;
	margin-right: 30%;
	margin-bottom: .5em;
	padding-top: 10px;
	padding-bottom: 10px;
	border-radius: 10px;
}

.container {
	background-color: var(--bgcolor);
	display: flex;
	max-width: 100%;
	justify-content: space-between;
	flex-direction: column;
	margin: 0;
	padding: 0;
	
}

.map {
	background-color: var(--bgcolor);
	margin-top: 1em;
	margin-bottom: 1em;
	margin-left: 1em;
	margin-right: 1em;
	padding: 1em;
	border: 4px solid var(--primarycolor);
	border-radius: 20px;
	color: var(--primarycolor);
	
	
	
}
#game .zone{
	background-color: var(--bgcolor);
	/*border: 4px solid var(--primarycolor);*/
	font-size: 35px;
	margin-left: 1em;
	margin-right: 1em;
	padding-left: 1em;
	padding-right: 1em;
	border: 5px solid var(--primarycolor);
	border-radius: 20px;
	margin-top: 1em;
	margin-bottom: 1em;
}

#game {
	display:flex;
	background-color: var(--bgcolor);
	justify-content: center;
	margin: 0;
	padding: 0;
}

.map .status {
	font-size: 40px;
	background: var(--bgcolor);
	color: var(--secondarycolor);
}

.maounted {
	margin: 0;
	padding: 0;
}


.player .payload-wrapper{
	color: var(--primarycolor);
	width: calc(100% - 20px);
}

#game .place-wrapper{
	background-color: var(--bgcolor);
	border: 0;
	margin: 3px;
	height: 120px;

}

#game .place {
	color: var(--typingtextcolor);
	text-align: center;
	font-size: 33px;

											
}
.interface-wrapper {
	background-color: var(--bgcolor);
}

.interface-wrapper .interface {
	background-color: var(--bgcolor);
}


.footer small {
	background-color: var(--bgcolor);
	color: rgba(134, 255, 5, .5);
	margin-right: 2px;
	padding-right: 2px;
	font-weight: bold;
}

#game span.complete {
	color: var(--primarycolor);
}

#game span.highlight {
	color: white;
	background: rgba(134, 255, 5, .5);
	border-radius: 10px;
}

#game span.error.highlight{
	color: black !important;
	background: var(--secondarycolor) !important;
}

.interface-wrapper input {
	background-color: var(--bgcolor);
	border: 2px solid var(--primarycolor);
	color: var(--primarycolor);
	border-radius: 30px;
	text-align: center;
}

.btn.btn-new-game {
	background-color: var(--primarycolor);
	color: var(--bgcolor);
	font-size: 18px;
	padding: 10px;
	border: 2px solid green;
	border-radius: 15px;
}

.game-result{
	background: var(--bgcolor);
	margin-left: 2em;
	width: 90%;
	height: 100%;
	padding: 20px;
	margin-right: 2em;
	margin-top: 1em;
	border: 4px solid var(--primarycolor);
	border-radius: 20px;
	

}

.game-result-wrapper{
	background: var(--bgcolor);
}
.more-game-result{
	background: var(--bgcolor);
}

.game-result table{
	background: var(--bgcolor);

}

.game-result.more-game-result thead{
	background: var(--bgcolor);

}

thead{
	background: var(--bgcolor);
	
}

br{
	display: none;
}

.game-result tbody > tr:nth-child(odd) > td, .game-result > tbody > tr:nth-child(odd) > th{
	background: var(--bgcolor);
	border: 0;
}

.game-result table > tbody > tr > td{
	border: 0;
}

.game-result .correct{
	color: rgb(205, 247, 160);
	font-size: 15px;
	font-weight: bold;
}

.btn.btn-new-game {
	cursor: url(https://cur.cursors-4u.net/others/oth-7/oth705.cur), auto !important;
}

.player > div.metadata-wrapper > div{
	font-size: 20px;
	padding-bottom: 2px;
}




}

@-moz-document url-prefix("https://10ff.net/login") {
:root {
	--primarycolor: rgb(134, 255, 5);
	--bgcolor: #161616; 
	
	cursor: url(https://cur.cursors-4u.net/others/oth-6/oth551.cur), auto !important;
}

body {

	background: var(--bgcolor);
	color: white;
	width: 100%;
	height: 100%;
	margin: 0;
	padding: 0;
	

	
}

body .container {
	display: flex;
	flex-direction: column;
	justify-content: center;
	position: fixed;
  	top: 50%;
  	left: 50%;
	max-width: 30em;
	width: 30em;
	height: 20em;
  	transform: translate(-50%, -50%);
  	-webkit-transform: translate(-50%, -50%);
  	-moz-transform: translate(-50%, -50%);
  	-o-transform: translate(-50%, -50%);
  	-ms-transform: translate(-50%, -50%);
  
  	font-size: 20px;
 	background-color: var(--bgcolor);
  	border: var(--primarycolor) 5px solid;
  	padding: 5px;
 	z-index: 100;	
	border-radius: 30px;

}

ul .error {
	margin: 0 0 30px 0;
}


#auth {	
	margin-top: 10px;
}

#username {
	background-color: var(--bgcolor);
	color: red;
	width: 60%;
	margin: 10px auto;
	margin-bottom: 30px;
	border: 3px solid var(--primarycolor);
	border-radius: 30px;
}

form input[type="submit"] {
	background-color: var(--primarycolor);
	color: var(--bgcolor);
	font-size: 22px;
	cursor: url(https://cur.cursors-4u.net/others/oth-7/oth705.cur), auto !important;
	
	font-weight: bold;
	border: 1px solid green;
}

form label {
	font-size: 22px;
	color: var(--primarycolor);
}
}
