css:
@-moz-document domain(boards.ign.com) {

h1 {
text-align:center; font-size: 13px; font-weight: bold; margin: 0 5px 5px 0 !important;
}

#boards_full_width_user_search {
display: none !important;
}

.networkBar {
display: none !important;
}

#boards_board_list_header {
width: 974px;
height: 65px !important;
background: #FFFFFF !important;
border-color: #FFFFFF;
border-style: solid;
border-width: 1px 0 0 0;
}

.boards_board_list_buttons {
float: left; 
height: 8px !important;
width: 140px !important; 
margin: 5px 0 15px 15px;       
font-size: 10px !important;
font-weight: bold;
}

#boards_board_list_table_header {
background: #000000 !important;
height: 20px !important;
padding: 5px 0 0 0 !important;
width: 974px;
font-size: 14px;
font-weight: bold;
float: left;
color: #FFFFFF !important;
}

.boards_board_list_row {
width: 974px;
float: left;
padding: 5px 0 5px 0 !important;
background: #DADAE2 !important;
}

.boards_board_list_row_over {
width: 974px;
float: left;
padding: 5px 0 5px 0 !important;
background: #DADAE2 !important;
}

.boards_board_list_row_subject {
width: 500px !important;
float: left;
padding: 0px 0 0 0 !important;
}

.boards_board_list_row_subject a:link 
{
font-size: 13px !important;
font-weight: bold;
color: #0F2556 !important;
}

.boards_board_list_row_subject a:visited
{
font-size: 13px !important;
color: #666666 !important;
}

.boards_thread_post_wrapper {
background: #D9DAE0;
border: 1px solid #999999 !important;
float: left;
width: 740px;
margin: 0 0 0 20px;
}

.boards_thread_user_profile_icon_80 {
width: 140px !important;
height: 140px !important;
float: right;
background: url(http://media.ignimgs.com/boards/img/grey/user_thread_profile_120_bg.jpg) no-repeat !important;
}
#boards_footer {
display: none !important;
}

#boards_full_width {
background: #DADAE2 !important;
}

.boards_thread_post_wrapper {
background: #FFFFFF !important;
border: 1px solid #999999;
float: left;
width: 740px;
margin: 0 0 0 20px;
}

#status_message {
background: #FDFBE4;
color: red;
font-weight: bold;
width: 954px !important;
height: 12px;
font-size: 15px !important;
text-align: center;
padding: 6px 10px 10px 10px;
margin: 0;
}

#boards_full_width_user_search_webforms {
display: none !important;
}
}
