css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #ff0000;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }


  .card--grit
  {
    background-image: url("https://cdn.pixabay.com/photo/2015/06/24/03/30/rainbow-819464_1280.jpg");
  }


  html
  {
    background: fixed url("https://i.pinimg.com/originals/81/6d/27/816d2708f2e7cdeee68ca8e6239b6459.jpg") no-repeat, #ffae00;
    box-sizing: border-box;
    color: #ffae00;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 200%;
  }


  .bg--gradient
  {
    background: top left repeat, linear-gradient(90deg, #5cb6ff 60%, #5cb6ff 200%);
  }


  .card--a
  {
    background-image: url("https://images-na.ssl-images-amazon.com/images/I/11fvWmpL17L.png");
    color: #5cb6ff;
    background-size: 200%;
  }


  .btn--primary
  {
    background: #0c0c0c;
    color: #5cb6ff;
  }


  .theme--pDefault .profile-title
  {
    color: #ff0000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-levelNum
  {
    color: #5cb6ff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-body
  {
    color: #000000;
  }
}


@-moz-document domain(nitrotype.com)
{


  .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }


  .dash-copy
  {
    color: #FFA500;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }


  ::selection
  {
    background-color: #5cb6ff;
    color: white;
  }


  .bg--gradient
  {
    background: url(https://cdn.pixabay.com/photo/2015/06/24/03/30/rainbow-819464_1280.jpg) no-repeat;
  }


  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.pixabay.com/photo/2015/06/24/03/30/rainbow-819464_1280.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #5cb6ff;
  }


  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #00ff00;
  }


  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.pixabay.com/photo/2015/06/24/03/30/rainbow-819464_1280.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ff0000 100%);
  }


  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://images.unsplash.com/photo-1481349518771-20055b2a7b24?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1109&q=80") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ff0000 200%);
  }


  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #5cb6ff;
  }
}
