css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #ff0000;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }


  .card--grit
  {
    background-image: url("https://www.financialdirector.co.uk/wp-content/uploads/2017/08/mcdonalds-arches.jpg");
  }


  html
  {
    background: fixed url("https://www.financialdirector.co.uk/wp-content/uploads/2017/08/mcdonalds-arches.jpg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #ff0000;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 200%;
  }


  .bg--gradient
  {
    background: top left repeat, linear-gradient(90deg, #101010 60%, #ffff00 100%);
  }





  .theme--pDefault .profile-title
  {
    color: #ffff00;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-levelNum
  {
    color: #000000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-body
  {
    color: #ff0000;
  }
}


@-moz-document domain(nitrotype.com)
{


  .dash-copyContainer
  {
    background: #ff0000;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }


  .dash-copy
  {
    color: #ffff00;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }


  ::selection
  {
    background-color: #ff0000;
    color: white;
  }


  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ff0000;
  }


  .theme--pDefault .profile-bgHead
  {
    background: url("https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/gettyimages-459305009-1486492640.jpg?crop=1.00xw:0.345xh;0,0.0746xh&resize=1200:*") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ff0000 100%);
  }


  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/gettyimages-459305009-1486492640.jpg?crop=1.00xw:0.345xh;0,0.0746xh&resize=1200:*") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }


  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #ffff00;
  }
}
