@-moz-document domain("rapidshare.com") {  

div[class="content with-sidebar on"], div.pro-features, .pro-download 
{ background: none !important;  border-radius: none !important; }  
#pro-area { margin-left: -220px  !important; }  
.main-nav, .share-btn,  .upload-btn, .open-contactlist, p#js_subtitle, .sidebar, .pro-promo, 
.pro-save, .footer, .c_toolbar, #js_greenarrow, .header 
{ display: none !important; }   

}
