/* ==UserStyle==
@name           pdd24 dark theme
@namespace      look at UserStyles.world
@version        0.0.1
@description    Темная тема для сайта pdd24.com / Dark theme for the site pdd24.com
@author         micecorn
==/UserStyle== */
@-moz-document domain("pdd24.com") {
    /* Навигационный бар (самый верх) */
    .navbar-static-top.navbar-default.navbar {
        background-color: #3e3e3e
    }
    .navbar-default .navbar-brand {
        color: #dbdbdb;
    }
    .navbar-default .navbar-brand:hover {
        color: #8f8f8f
    }
    .navbar-default .navbar-nav > li > a {
        color: #dbdbdb
    }
    .navbar-default .navbar-nav > .active > a,
    .navbar-default .navbar-nav > .active > a:focus,
    .navbar-default .navbar-nav > .active > a:hover {
        background-color: #272727;
        color: #dbdbdb
    }
    .navbar-default .nav > li > a:hover {
        color: #8f8f8f
    }
    .navbar-default {
        border-color: #272727
    }

    /* Тело сайта (центральная часть) */
    body {
        background-color: #272727;
        color: #d7d7d7
    }
    .myheader h1,
    .myheader .h1 {
        color: #3886c8
    }
    a.list-group-item,
    button.list-group-item {
        color: #e2e2e2;
        background-color: #313131;
        border-color: #1a1a1a
    }
    a.list-group-item:focus,
    a.list-group-item:hover,
    button.list-group-item:focus,
    button.list-group-item:hover {
        color: #e2e2e2;
        background-color: #535353;
    }
    .btn-default {
        background-color: #404040;
        border-color: #5e5e5e;
        border: none;
        color: #e8e8e8
    }
    .panel-body {
        background-color: #494949;
    }
    .panel-primary {
        border-color: #272727;
    }
    .bs-example {
        background-color: transparent
    }
    .panel-footer {
        background-color: #383838;
        border-top: 1px solid #2f2f2f;
    }
    .nav-tabs > li.active > a,
    .nav-tabs > li.active > a:focus,
    .nav-tabs > li.active > a:hover {
        background-color: #272727;
        color: #e8e8e8
    }
    .nav > li > a:focus,
    .nav > li > a:hover {
        background-color: #404040
    }
    a {
        color: #fff
    }
    a:focus,
    a:hover {
        color: #4ca0ea;
    }
    .form-control {
        background-color: #717171;
        border-color: #272727;
        color: #fff
    }
    input {
        color: #fff;
        background-color: #555;
        border-color: transparent
    }
    .text-primary {
        color: #4ca0ea
    }
    .text-success {
        color: #6cc56e
    }
    .list-group-item {
        background-color: #444;
        border-color: #1a1a1a;
        color: #e8e8e8
    }
    .text-warning {
        color: #d7d7d7;
    }
    .modal-content {
        background-color: #424242
    }
    .modal-footer {
        border-top: none
    }
    a.list-group-item .list-group-item-heading,
    button.list-group-item .list-group-item-heading {
        color: #e8e8e8
    }
    .btn:hover {
        color: #717171
    }
    .btn-default:hover {
        background-color: #404040;
        color: #c4c4c4
    }
    textarea {
        background-color: #333
    }
    textSearchCity {
        color: #fff
    }
    div.suggestPopupContent div table tbody tr td.item {
        background-color: #7a7a7a;
        border-color: #4d4d4d;
        color: #eee
    }
    div.suggestPopupContent div table tbody tr td.item-selected {
        padding: 12px;
        background-color: #5a5a5a
    }

    /* Страничка с ПДД */
    .dropdown-menu > li > a {
        color: #e8e8e8
    }
    .dropdown-menu {
        background-color: #404040
    }

    /* Результаты */
    .panel {
        background-color: #494949
    }
}
