css:
@-moz-document url-prefix("https://tusecreto.io") {
/* main.783bfb38.css | https://tusecreto.io/static/css/main.783bfb38.css */

.navbar-fixed-bottom {
    background: #282828;
    border-bottom: 1px solid #ddd0;
}

body {
    color: #282828;
}

body {
    background: #424242;
}

.top-heading h4 span {
    background: #202020;
}

.top-heading h4 {
    color: #888;
}

.secret-2 .secret-heading, .secret-2 .secret-heading a {
    background-color: #460d21;
    color: #ff8bb7;
}

.secret-1 .secret-heading, .secret-1 .secret-heading a {
    background-color: #23475b;
    color: #6fc5f7;
}

.secret-0 .secret-heading, .secret-0 .secret-heading a {
    background-color: #202020;
    color: #808080;
}

.secret-body {
    color: #c1c1c1;
    background: #404040;
}

body {
    color: #a6a6a6;
}

body {
    background: #202020;
}

.secret-meta {
    border-bottom: 1px solid #eee0;
    color: #a6a6a6;
    background: #333;
}

.secret-footer {
    background: #404040;
}

.navbar-fixed-bottom .nav-pills > li > a {
    color: #737373;
}

.navbar-fixed-bottom .nav-pills > li > a small {
    color: #919191;
}

.navbar {
    border: 1px solid transparent;
}

.alert-warning {
    color: #b78065;
    background-color: #332f18;
}

.secret-filters > li > a {
    color: #a6a6a6;
}

.secret-filters > li > a:active, .secret-filters > li > a:focus, .secret-filters > li > a:hover {
    background: #313131;
}

.secret-filters > li > a .fa-toggle-off {
    color: #7b7b7b;
}

#top-navfilter {
    background: #32570d;
    color: #919191;
}

.modal-footer, .modal-header {
    background: #1e1e1e;
}

.modal-header {
    border-bottom: 1px solid #4d4d4d;
}

.modal-body {
    background: #1e1e1e;
}

.modal-footer {
    border-top: 1px solid transparent;
}

hr {
    border-top: 1px solid transparent;
}

.close {
    color: #fff;
    text-shadow: 0 1px 0 #000;
}

.form-control {
    background-color: #222;
    border: 1px solid #000;
    color: #9e9e9e;
}

.react-autosuggest__container--open .react-autosuggest__suggestions-container {
    background-color: #191919;
    border: 1px solid #000;
}

.react-autosuggest__suggestion {
    border-top: 1px solid #000;
    color: #9e9e9e;
}
}
