css:
@-moz-document domain(theqoo.net)
{/*상단 메뉴 배경색 변경*/
 .header, .gnb .nav li a:hover, .gnb .nav li a:focus, .da-dots span, .da-arrows span, .bootstrap_body .btn-group .btn:hover, .da-slide .da-link:hover, .h2_widget span, .camera_wrap .camera_pag .camera_pag_ul li.cameracurrent>span, .owl-theme .owl-controls .owl-page.active span, .owl-theme .owl-controls.clickable .owl-page:hover span, #scrollUp:hover {
    background-color: #FF6347;
}
/*즐찾 메뉴*/
 .favorites_menu {
    background-color: #FF6347;
}
/*게시판찾기 메뉴 */
 .board_search_bar input[type=text] {
    background-color: #FF6347;
}
/*목록 스크랩 위 선 */
 .rd_fnt, .rd_ft_nav {
    border-top: 1px solid #FFE4B5;
}
/*목록 스크랩 */
 .theqoo_btn, .theqoo_board_btn, .theqoo_btn.blue, .theqoo_board_btn.blue {
    background-color: #FF6347;
}
/*배경 전체*/
 #body {
    background: #FFE4B5 !important;
}
/*무명의 더쿠 _ 조회수 _ 댓글수*/
 .bd .rd_hd .btm_area {
    background-color: #FFE4B5;
}
/*잡담 온에어 옆에 구분선*/
 .bd .theqoo_document_header strong:after {
    background-color: #FFE4B5;
}
/*제목*/
 .bd .theqoo_document_header {
    background-color: #FFE4B5;
    border-bottom: 1px solid #FFE4B5;
}
/*1덬 위의 선*/
 .bd .comment_header_bar {
    border-bottom: 1px solid #FFE4B5;
}
/*댓글의 무명의 더쿠 글씨 색 변경*/
 .fdb_itm .meta a {
    font-weight: 700;
    color: #800000;
}

/*댓글 사이 선*/

.fdb_itm{
    border-top: 1px solid #FFE4B5;
    border-bottom: 1px solid #FFE4B5;
}

/*새댓글 확인하기*/
 .bd .refresh_new_comment {
    border-top: 1px solid #FFE4B5;
    background-color: #FFE4B5;
}
/*새댓글 확인하기 밑에 선*/
 div.cmt_editor {
    border-top: 1px solid #FFE4B5;
}
/*html 써있는 댓글창 */
 .xpress-editor .tool {
    background: #FFE4B5;
}
/* 댓글 쪽 배경 */
 .cmt_editor {
    background-color: #FFE4B5;
}
/* 댓글 쓰기 (단축키) */
 div.cmt_editor div.edit_opt .bd_btn {
    background-color: #FF6347;
}
/* 댓글 쪽 배경 선 */
 .xpress-editor #smart_content {
    border:#FFE4B5;
}
/*입력창 크기 자동조절*/
 .xpress-editor .input_control {
    background: #FFE4B5;
    border-top: 1px solid #FFE4B5;
}
/*사진주소를입력해주세요*/
 .tqu-input-text {
    background-color: #FFE4B5 !important;
}
/*제목 날짜*/
 table.theqoo_board_table thead tr th {
    border-bottom: 1px solid #FFE4B5;
}
.bd_tb_lst th {
    box-shadow: inset 0 -1px 0 #FFE4B5;
}
/*공지*/
 .bd_tb_lst .notice td, .bd_zine li.notice, .bd_tb_lst .select, .bd_tb_lst tbody tr:hover {
    background-color: #FFDAB9;
    border:#FFE4B5;
    background: #FFDAB9;
}
/* 글 사이사이 선*/
 .bd_tb_lst td, .bd_zine li {
    border-bottom: 1px solid #FFE4B5;
}
/* 맨아래 검색*/
 .theqoo_board_search input[type=submit] {
    background: #FF6347 !important;
    border:#FF6347;
}
/* 번호 선택 */
 .theqoo_pagination ul li.active a {
    background: #FF6347;
}
/* 카테고리 스포츠 이런 */
 #cate_index_pc table.main td ul.list>li.cate_list_header {
    background-color: #FF6347;
}
/* 동접자수 표시 */
 .lnb_section {
    background-color: #FFE4B5;
}
/* 동접자수 표시 */
 .theqoo-counter {
    background-color: #FF6347;
    border: #FF6347;
}
/* 하단부 */


/*아마 광고 삭제*/
.header-banner, #hongboInfoList,.naver-ad-list, .related-list, .sympathyList, .recentlyList, .top_google_ad_space , .document_list_google_ad_space, .board_content_google_ad{
    display: none;
}

.div-rs-under, .div-rs-header,.div-rs-main-top, .div-rs-main-center, .div-rs-side-foot , #google-center-div {
    display: none;
}

#bnr{
    display: none; 
}
.div-rs-header {display: none; }
.div-rs-under {display: none; }
.post-hr {display: none; }
.comment-head {display: none; }
.comment-hr {display: none; }


/*글 쓸 때 삭제*/
.bd_wrt div.regist{
    border-top: 1px solid #FFE4B5;

}
/*댓글 더보기*/
#cmtPosition .show_more{
  background:#FFE4B5;
  border:#FFE4B5;
}
/*개인정보 메뉴*/
ul.nav.nav-tabs{
    background-color: #FF6347; 
    border:#FFE4B5;
}

.xm .table.even th, .xm .table.even td{
    border-bottom: none;
}
#border{
    background:#FFE4B5;
}
    
/*모바일*/

/*메인*/

/*로그인 화면*/
header.main, .member-menu-top .profile-image, .member-menu-logged ul, .main-logo, .login-wrap, .member-header-wrap, .read-control li:not(.comment-num) a, .read-control li:not(.comment-num) button, .flatBoard .login-wrap, .m-element .notice-list li .notice-text, .m-imagenews .info span.category, .swiper-active-switch, .m-ranking .rank-num, .main-image-wrap, .main-normal-image{
    background-color: #FF6347; 
}

.btDark, .flatMember .btSubmit, .flatBoard .btSubmit, .login-body .btLogin, .m-element nav ul, .m-element section h3, .lang-list, .main-search button{
    background-color: #FF6347; 
}
/*목록 배경*/
.btBasic, .global-search, #menu-wrap, .login-wrap input[type="text"], .login-wrap input[type="password"], .login-wrap input[type="email"], .login-wrap input[type="url"], .login-wrap input[type="file"], .terms-container .terms-body, .m-list-reply, .bubble, .content, button.thumbText, .comment-view-vote, .bottom-menu1 li .btBasic.on, .category-list, .searchinput2, .paging2 a.btBasic, .read-declare button{
    background-color: #FFE4B5;
}
/*공지*/
.btAdmin, .bottom-menu1 li .btBasic, .gnb li ul, .info-table th, header.list-header .search-area, .read-file, .m-element .notice-list, .signup-form .radio-wrap, .info-body .pass_ex, .saved-body .savedContent, .comment-write textarea, .input100, .xv th, .write-form input, .write-form textarea, .origin-body, .flatBoard .message-body, .login-body, .signup-body, .find-body, .declare{
    background-color: #FFDAB9;
}
/*검색어*/
.legacy_search_bar{
    background-color: #FFE4B5;
    border: #FFE4B5;
}
/*하단부*/
footer.main-footer{
    border: #FFE4B5;
}
footer{
    background-color: #FFE4B5;

}
.footer_bar>div{
    background-color: #FFE4B5;
    border: #FFE4B5;
}
/*댓글 공유 목록*/
#comment .read-control{
    background-color: #FFE4B5;
    border: #FFE4B5;
}
/*새댓글*/
.refresh_new_comment{
    background-color: #FFE4B5;
    border: #FFE4B5;
}

}
